import React, { useState } from 'react'
import {
  FlexRowContainer,
  FlexRowInnerLeft,
  FlexRowInnerRight,
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  InputBox,
  MainContainer,
  Margin24,
  Margin36,
  SubTitle,
} from '../../../../../component/styledComponent'
import styled from 'styled-components'

const Time = styled.div`
  width: calc(50% - 18px);
`
const TimeDivider = styled.div`
  width: 36px;
  margin-top: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
`
const DayContainer = styled.div`
  flex: 1;
  text-align: left;
  background-color: #4cd3d3;
`

const days = [
  '월요일',
  '화요일',
  '수요일',
  '목요일',
  '금요일',
  '토요일',
  '일요일',
]
interface DayProps {
  weekday: string | undefined
  index: number
}
const Day = (props: DayProps) => {
  const { weekday, index } = props
  return <DayContainer>{days[index]}</DayContainer>
}

export const InnerScheduleRegist = () => {
  const [title, setTitle] = useState<string>()
  const [subtitle, setSubtitle] = useState<string>()
  const [weekday, setWeekday] = useState<string>()
  const [place, setPlace] = useState<string>()
  const [mc, setMc] = useState<string>()
  const [target, setTarget] = useState<string>()
  const [reference, setReference] = useState<string>()
  const [startTime, setStartTime] = useState<string>()
  const [endTime, setEndTime] = useState<string>()
  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>예배 일정 등록</InnerBoxTitle>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <Margin36 />
        <FlexRowContainer>
          <FlexRowInnerLeft>
            <SubTitle>예배명</SubTitle>
            <InputBox
              type="text"
              maxLength={20}
              value={title}
              placeholder="예배명 입력"
              onChange={e => setTitle(e.target.value)}
            />
          </FlexRowInnerLeft>
          <FlexRowInnerRight>
            <SubTitle>소제목</SubTitle>
            <InputBox
              type="text"
              maxLength={20}
              value={subtitle}
              placeholder="소제목 입력"
              onChange={e => setSubtitle(e.target.value)}
            />
          </FlexRowInnerRight>
        </FlexRowContainer>
        <Margin36 />
        <FlexRowContainer>
          <Time>
            <SubTitle>시간</SubTitle>
            <InputBox
              type="text"
              maxLength={20}
              value={startTime}
              placeholder="00.01.01"
              onChange={e => setStartTime(e.target.value)}
            />
          </Time>
          <TimeDivider>~</TimeDivider>
          <Time>
            <Margin24 />
            <InputBox
              type="text"
              maxLength={20}
              value={endTime}
              placeholder="00.01.01"
              onChange={e => setEndTime(e.target.value)}
            />
          </Time>
        </FlexRowContainer>
        <Margin36 />
        <SubTitle>요일</SubTitle>
        <FlexRowContainer>
          {days.map((day, index) => {
            return <Day weekday={weekday} index={index} />
          })}
        </FlexRowContainer>
      </InnerBoxContainer>
    </MainContainer>
  )
}
