import React, { useEffect, useState } from 'react'
import { Week } from '../../../../../hooks/useMonth'
import styled from 'styled-components'

const DayContainer = styled.div`
  width: 128px;
  height: 26px;
`

const WeekContainer = styled.div`
  height: 124px;
  display: flex;
  flex-direction: row;
`
const DateText = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`

const TestBox = styled.div`
box-sizing: border-box;
display: flex;
flex-direction: row;
align-items: center;
padding: 2px 4px;
gap: 8px;

position: absolute;
width: 124px;
height: 26px;
left: calc(50% - 124px/2);
top: 8px;

background: #FFFFFF;

border: 1px solid #5500DD;
border - radius: 8px;
`
interface DayProps {
  index: number
  date: number
  year: number
  month: number
  isCurrentMonth: boolean
}
interface WeekViewProps {
  week: Array<Week>
  year: number
  month: number
}

const test_calendar = [
  {
    title: 'title1',
    content: 'content2',
    start_date: '2023-02-28',
    end_date: '',
  },
  {
    title: 'title2',
    content: 'content2',
    start_date: '2023-03-01',
    end_date: '2023-03-05',
  },
  {
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '',
  },
]

const Day = (props: DayProps) => {
  const { index, date, isCurrentMonth, year, month } = props
  return (
    <DayContainer>
      <DateText
        style={{
          color: !isCurrentMonth
            ? '#c1c2c3'
            : index === 0
            ? '#D40030'
            : '#000000',
        }}
      >
        {date}
      </DateText>
    </DayContainer>
  )
}

export const WeekView = (props: WeekViewProps) => {
  const { week, year, month } = props

  return (
    <>
      <WeekContainer>
        {week &&
          week.map((day, index) => {
            return (
              <Day
                index={index}
                date={day.date}
                year={year}
                month={month}
                isCurrentMonth={day.isCurrentMonth}
              />
            )
          })}
        <TestBox>123</TestBox>
      </WeekContainer>
    </>
  )
}
