import React, { useEffect, useRef, useState } from 'react'
import styled from 'styled-components'
import { setAuthToken } from '../../../../../../api'
import { getPastor } from '../../../../../../api/admin-pastor'
import InfiniteScroll from 'react-infinite-scroll-component'
import { PastorBox } from '../../pastorBox'
import { InnerBoxHeader, InnerBoxTitle, Margin24, Margin36 } from '../../../../../../component/styledComponent'
import { InfoBox } from '../../../../../../component/box/info'
import { PastorBoxOrder } from '../../pastorBox_order'

const ModalContentContainer = styled.div`
  // max-height: 80vh;
  overflow-y: auto;
`

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`

const ModalWrapper = styled.div`
  position: relative;
  width: 714px;
  background-color: #ffffff;
  border-radius: 20px;
`

const ModalCloseButton = styled.button`
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #ffffff;
  border: none;
  cursor: pointer;
`

const PastorContainer = styled.div`
  max-height: 450px;
  // overflow: scroll;
`
const NoticeModalContent = styled.div`
  // display: flex;
  width: 100%;
  // padding: 0 36px 0 36px;
  // flex-direction: column;
  justify-content: center;
  align-items: center;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`

const DataContainer = styled.div`
  padding: 0 36px 0 36px;
  gap: 48px;
  justify-content: space-between;
`
interface PastorData {
  church_pastor_id: string
  order: string
  name: string
  role: string
  image: string
}
interface NoticeModalProps {
  onCloseModal: () => void
}

export const PatchOrderModal = (props: NoticeModalProps) => {
  const { onCloseModal } = props
  const offset = useRef<number>(0)
  const maxCount = useRef<number>(0)
  const [pastorData, setPastorData] = useState<Array<PastorData>>([
    {
      church_pastor_id: '',
      order: '',
      name: '',
      role: '',
      image: '',
    },
  ])

  const handleCloseModal = () => {
    onCloseModal()
  }

  const handleWrapperClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    event.stopPropagation()
  }

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    const fetchData = async () => {
      const response = await getPastor(offset.current)
      setPastorData(response.payload.church_pastor_list.results)
      maxCount.current = response.payload.church_pastor_list.total_count
    }
    fetchData()
  }, [])

  const loadMoreItem = async () => {
    if (offset.current + 15 < maxCount.current) {
      const response = await getPastor(offset.current)
      setPastorData([
        ...pastorData,
        response.payload.church_pastor_list.results,
      ])
    }
  }

  useEffect(() => {
    document.body.style.cssText = `
      position: fixed; 
      top: -${window.scrollY}px;
      overflow-y: scroll;
      width: 100%;`
    return () => {
      const scrollY = document.body.style.top
      document.body.style.cssText = ''
      window.scrollTo(0, parseInt(scrollY || '0', 10) * -1)
    }
  }, [])

  return (
    <ModalOverlay onClick={handleCloseModal}>
      <ModalWrapper onClick={handleWrapperClick}>
        <Margin36 />
        <InnerBoxHeader>
          <InnerBoxTitle>섬기는 이 순서변경</InnerBoxTitle>
          <ModalCloseButton onClick={onCloseModal}>
            <img src="source/icon/close.svg" alt="close" />
          </ModalCloseButton>
        </InnerBoxHeader>
        <Margin24 style={{ background: "#ffffff" }} />
        <DataContainer>
          <InfoBox text='순서를 변경하고 싶다면 숫자를 변경하거나, 박스를 잡고 순서를 변경할 수 있어요.' />
        </DataContainer>
        <Margin24 style={{ background: "#ffffff" }} />
        <ModalContentContainer>
          {/* <NoticeModalContent> */}
          <PastorContainer>
            {pastorData.length !== 0 && (
              <InfiniteScroll
                dataLength={pastorData.length}
                next={loadMoreItem}
                hasMore={offset.current + 15 < maxCount.current ? true : false}
                loader={<h4>Loading...</h4>}
              >
                {pastorData.map((pastor, index) => {
                  return (
                    <PastorBoxOrder
                      index={index}
                      image={pastor.image || 'source/no_image.svg'}
                      name={pastor.name}
                      role={pastor.role}
                      saveImageFile={loadMoreItem}
                      changeName={loadMoreItem}
                      changeRole={loadMoreItem}
                    />
                  )
                })}
              </InfiniteScroll>
            )}
          </PastorContainer>
          {/* </NoticeModalContent> */}
        </ModalContentContainer>
      </ModalWrapper>
    </ModalOverlay>
  )
}
