import React, { useRef, useState } from "react";
import styled from "styled-components";

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWrapper = styled.div`
  position: relative;
  width: 700px;
  height: 600px;
  background-color: #ffffff;
  border-radius: 20px;
`;

const ModalCloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #FFFFFF;
  border: none;
  cursor: pointer;
`;

const NoticeModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const ButtonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${ButtonStyle}
`;

const DeleteButton = styled.button`
  ${ButtonStyle}
`;

const Image = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;


interface NoticeModalProps {
  onCloseModal: () => void;
  church_notice_id: string;
  title: string;
  content: string;
  notice_image: string;
}

export const NoticeModal = (props: NoticeModalProps) => {
  const { onCloseModal, church_notice_id, title, content, notice_image } = props;

  const [image, setImage] = useState(notice_image);
  const [uploadImage, setUploadImage] = useState<any>([]);
  const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current)
      imageFile.current.click()
  }
  const saveImageFile = (event: any) => {
    if (event.target.files !== null && event.target.files[0] !== undefined) {
      setUploadImage(event.target.files[0])
      setImage(URL.createObjectURL(event.target.files[0]))
    }
  }
  const deleteImageFile = () => {
    setImage("")
    setUploadImage([false])
  }

    return (
        <ModalOverlay >
            <ModalWrapper >
                <ModalCloseButton onClick={onCloseModal}><img src="source/icon/close.svg" alt="close" /></ModalCloseButton>
          <NoticeModalContent>
          <Image><img src={image || "source/no_image.svg"} alt="교회 로고" /></Image>
            <UploadButton
                style={{
                  background: "#ffffff" ,
                  border: "1px solid #5500DD",
                  color: "#5500DD" ,
                }}
                onClick={openImageFile}
              >
                <input type='file' id='file' ref={imageFile} style={{ display: 'none' }} onChange={saveImageFile} />
                업로드
              </UploadButton>
              <DeleteButton
                style={{
                  background: "#ffffff" ,
                  border: "1px solid #D40030",
                  color: "#D40030" ,
                }}
                onClick={deleteImageFile}
              >
                삭제
              </DeleteButton>
                  <h2>{church_notice_id}</h2>
                  <h2>{title}</h2>
                  <h2>{content}</h2>

                </NoticeModalContent>
            </ModalWrapper>
        </ModalOverlay>
    );
};
