import axios from "axios";
import {instance} from '../index';
import { type } from "os";

export const getChurchInfo = async () => {
  try {
    const response = await instance.get("administrators/churchs/info/");
    return response.data;
  } catch (error) {
    console.error(error);
  }
};

export const registPastor = async () => {
  try {
    const response = await instance.post("administrators/churchs/pastor/");
    return response.data;
  } catch (error) {
    console.error(error);
  }
};

export const patchChurchInfo = async (logo:any,thumbnail:any,churchName:string|undefined, denominationName:string|undefined, contackNumber:string|undefined, introductionTitle:string|undefined, introductionContent:string|undefined, address:string|undefined, detailAddress:string|undefined) => {
  const formData = new FormData();
    churchName !== undefined && formData.append('church_name', churchName);
    denominationName!== undefined && formData.append('denomination_name', denominationName);
    contackNumber!== undefined && formData.append('contact_number', contackNumber);
    introductionTitle!== undefined && formData.append('introduction_title', introductionTitle);
    introductionContent!== undefined && formData.append('introduction_content', introductionContent);
    address!== undefined && formData.append('address', address);
    detailAddress!== undefined && formData.append('detail_address', detailAddress);
    logo.length !== 0 && typeof logo[0] === typeof true ? formData.append('is_delete_logo', "false") : formData.append('logo', logo, logo.name);
    thumbnail.length!== 0 && typeof thumbnail[0] === typeof true ? formData.append('is_delete_thumbnail', "false"): formData.append('thumbnail', thumbnail, thumbnail.name);
  try {
    const response = await instance.patch("administrators/churchs/info/",formData);
    return response.data;
  } catch (error) {
    console.error(error);
  }
};

export const patchChurchAddress = async (
  parking:string,
  onCar:string,
  publicTransport:string,
  shuttleBus:string,
) => {
  try {
    const formData = new FormData();
    parking !== undefined && formData.append("parking", parking)
    onCar !== undefined && formData.append("on_car", onCar)
    publicTransport !== undefined && formData.append("public_transport", publicTransport)
    shuttleBus !== undefined && formData.append("shuttle_bus",shuttleBus)
    const response = await instance.patch("administrators/churchs/directions/", formData);
    return response.data;
  } catch (error: any) {
    console.error(error?.response);
  }
};
