interface WeeklyPropType {
  refresh: boolean
  setRefresh: Dispatch<SetStateAction<boolean>>
}

export const InnerRegistPage = (props: WeeklyPropType) => {
  const { setRefresh, refresh } = props
  var today = new Date()
  const monthForm = (month: string) => {
    if (month.length === 2) {
      return month
    }
    return '0' + month
  }
  var year = today.getFullYear()
  const yearOptions = [
    { value: (year - 1).toString(), label: (year - 1).toString() + '년' },
    { value: year.toString(), label: year.toString() + '년' },
    { value: (year + 1).toString(), label: (year + 1).toString() + '년' },
  ]
  const monthOptions = [
    { value: '01', label: '1월' },
    { value: '02', label: '2월' },
    { value: '03', label: '3월' },
    { value: '04', label: '4월' },
    { value: '05', label: '5월' },
    { value: '06', label: '6월' },
    { value: '07', label: '7월' },
    { value: '08', label: '8월' },
    { value: '09', label: '9월' },
    { value: '10', label: '10월' },
    { value: '11', label: '11월' },
    { value: '12', label: '12월' },
  ]

  const [weeklyTitle, setWeeklyTitle] = useState('')
  const [weeklyImage, setWeeklyImage] = useState('')
  const [weeklyYear, setWeeklyYear] = useState(today.getFullYear().toString())
  const [weeklyMonth, setWeeklyMonth] = useState(
    monthForm((today.getMonth() + 1).toString())
  )
  const [weeklyTitleCount, setWeeklyTitleCount] = useState(0)
  const [uploadImage, setUploadImage] = useState<any>([])
  const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current) imageFile.current.click()
  }
  const saveImageFile = (event: any) => {
    if (event.target.files !== null && event.target.files[0] !== undefined) {
      setUploadImage(event.target.files[0])
      setWeeklyImage(URL.createObjectURL(event.target.files[0]))
    }
  }
  const deleteImageFile = () => {
    setWeeklyImage('')
    setUploadImage([false])
  }
  const handleEdit = async () => {
    const response = await createChurchWeekly(
      uploadImage,
      weeklyTitle,
      weeklyYear,
      weeklyMonth
    )
    response?.success === true && setRefresh(!refresh)
    response?.success === true && setWeeklyTitle('')
    response?.success === true && setWeeklyImage('')
  }

  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>주보 등록</InnerBoxTitle>
        <ModifyButtonBox>
          <ModifyButton active={weeklyTitle !== ''} onClick={handleEdit}>
            등록
          </ModifyButton>
        </ModifyButtonBox>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <div style={{ paddingTop: 36 }} />
        <FlexRowContainer>
          <FlexRowInnerLeft>
            <SubTitle>
              제목<SubTitleColorText> *</SubTitleColorText>
            </SubTitle>
            <InputBoxWithCount>
              <InputBox
                type="text"
                maxLength={20}
                value={weeklyTitle}
                placeholder="제목 입력"
                onChange={e => {
                  setWeeklyTitle(e.target.value)
                  setWeeklyTitleCount(e.target.value.length)
                }}
              />
              <InputCount>({weeklyTitleCount}/20)</InputCount>
            </InputBoxWithCount>
            <Margin36 />
            <SubTitle>
              날짜<SubTitleColorText> *</SubTitleColorText>
            </SubTitle>
            <WeeklyDateSelect>
              <Select
                options={yearOptions}
                defaultValue={yearOptions[1]}
                onChange={e => e && setWeeklyYear(e?.value)}
                isSearchable={false}
                theme={theme => ({
                  // change color
                  ...theme,
                  borderRadius: 16,
                  colors: {
                    ...theme.colors,
                    primary25: '#F0E6FD',
                    primary: '#5500DD',
                    neutral20: '#000000',
                  },
                })}
              />
            </WeeklyDateSelect>
            <WeeklyDateSelect>
              <Select
                options={monthOptions}
                defaultValue={monthOptions[today.getMonth()]}
                onChange={e => e && setWeeklyMonth(e?.value)}
                isSearchable={false}
                theme={theme => ({
                  // change color
                  ...theme,
                  borderRadius: 16,
                  colors: {
                    ...theme.colors,
                    primary25: '#F0E6FD',
                    primary: '#5500DD',
                    neutral20: '#000000',
                  },
                })}
              />
            </WeeklyDateSelect>
          </FlexRowInnerLeft>
          <FlexRowInnerRight>
            <SubTitle>
              주보 이미지<SubTitleColorText> *</SubTitleColorText>
            </SubTitle>
            <Margin16 />
            <InfoBox text="주보 이미지를 스캔하거나 촬영하여 등록해주세요." />

            <WeeklyImage>
              <img
                src={weeklyImage || 'source/no_image.svg'}
                alt="공지 이미지"
              />
            </WeeklyImage>
            <ButtonBox>
              <UploadButton
                style={{
                  background: '#ffffff',
                  border: '1px solid #5500DD',
                  color: '#5500DD',
                }}
                onClick={openImageFile}
              >
                <input
                  type="file"
                  id="file"
                  ref={imageFile}
                  style={{ display: 'none' }}
                  onChange={saveImageFile}
                />
                업로드
              </UploadButton>
              <DeleteButton
                style={{
                  background: '#ffffff',
                  border: '1px solid #D40030',
                  color: '#D40030',
                }}
                onClick={deleteImageFile}
              >
                삭제
              </DeleteButton>
            </ButtonBox>
          </FlexRowInnerRight>
        </FlexRowContainer>

        <div
          style={{
            height: '48px',
          }}
        />
      </InnerBoxContainer>
      <div
        style={{
          height: '24px',
        }}
      />
    </MainContainer>
  )
}
