import React, {
  Dispatch,
  SetStateAction,
  useEffect,
  useRef,
  useState,
} from 'react'
import styled from 'styled-components'
import { Page } from '../../../../component/page'
import { usePage } from '../../../../hooks/usePage'
import { useDebounce } from '../../../../hooks/useDebounce'
import {
  getConfirmMember,
  getMember,
  kickOffMember,
  patchConfirmMember,
} from '../../../../api/admin-member'
import { MemberBox } from '../memberBox'
import { setAuthToken } from '../../../../api'
import { MemberHeader } from '../memberHeader'
import { Modal } from '../modal'
import { EmptyText } from '../../../../component/empty_text'

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`
const MemberContainer = styled.div`
  margin-left: 36px;
  margin-right: 36px;
`

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`

const InputBox = styled.input`
  margin-top: 36px;
  margin-bottom: 36px;
  width: 100%;
  box-sizing: border-box;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`
const DataBox = styled.div`
  width: 100%;
`
const ButtonContainer = styled.div`
  display: flex;
  flex-direction: row;
`
const RejectButton = styled.button`
  color: #ffffff;
  background-color: #d40030;
  width: 120px;
  height: 40px;
  border: none;
  cursor: pointer;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
`
const SuccessButton = styled.button`
  color: #ffffff;
  background-color: #5500dd;
  width: 120px;
  height: 40px;
  border: none;
  cursor: pointer;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  margin-right: 16px;
`

interface Member {
  account_id: string
  birth_date: string
  gender: string
  name: string
  phone_number: string
}
interface MemberPageProps {
  refresh: boolean
  setRefresh: Dispatch<SetStateAction<boolean>>
}

const pagePerLimit = 10
export const InnerConfirmMemberPage = (props: MemberPageProps) => {
  const { refresh, setRefresh } = props
  const [memberPage, setMemberPage] = useState(0)
  const [memberData, setMemberData] = useState<Array<Member>>([])
  const [keyword, setKeyword] = useState('')

  const { pageList, currentPage, getPrevPage, getNextPage, clickPage } =
    usePage({ maxPage: memberPage })
  const { debounceCallback, isLoading } = useDebounce({ time: 1000 })
  const [clickedAccountList, setClickedAccountList] = useState<Array<string>>(
    []
  )
  const [showRejectModal, setShowRejectModal] = useState(false)
  const [showSuccessModal, setShowSuccessModal] = useState(false)

  const firstLoading = useRef<boolean>(false)

  const getChurchMember = async (page: number, keyword: string) => {
    const response = await getConfirmMember((page - 1) * pagePerLimit, keyword)
    setMemberData(response.payload.confirm_members.results)
    setMemberPage(
      Math.ceil(response.payload.confirm_members.total_count / pagePerLimit)
    )
    firstLoading.current = true
  }
  useEffect(() => {
    if (memberData.length !== 0) {
      getChurchMember(currentPage, keyword)
    }
  }, [refresh])

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    getChurchMember(1, '')
  }, [])

  useEffect(() => {
    if (memberPage !== 0) {
      debounceCallback(() => {
        setMemberPage(0)
        getChurchMember(1, keyword)
      })
    }
  }, [keyword])

  const onClickMemberBox = (account_id: string) => {
    if (clickedAccountList.indexOf(account_id) > -1) {
      setClickedAccountList(clickedAccountList.filter(id => id !== account_id))
    } else {
      setClickedAccountList([...clickedAccountList, account_id])
    }
  }

  const onClickMemberHeader = () => {
    if (clickedAccountList.length === memberData.length) {
      setClickedAccountList([])
    } else {
      var allAccountList = memberData.map(member => member.account_id)
      setClickedAccountList([...allAccountList])
    }
  }

  const clickRejectButton = async () => {
    await patchConfirmMember(clickedAccountList, 'reject')
    setClickedAccountList([])
    setShowRejectModal(false)
    setRefresh(!refresh)
  }
  const clickSuccessButton = async () => {
    await patchConfirmMember(clickedAccountList, 'success')
    setClickedAccountList([])
    setShowSuccessModal(false)
    setRefresh(!refresh)
  }

  return (
    <Container>
      <Header>
        <MainTitleText>등록 대기 명단</MainTitleText>
        <ButtonContainer>
          <SuccessButton onClick={() => setShowSuccessModal(true)}>
            승인
          </SuccessButton>
          <RejectButton onClick={() => setShowRejectModal(true)}>
            거절
          </RejectButton>
        </ButtonContainer>
      </Header>

      <DataBoxContainer>
        <MemberContainer>
          <InputBox
            type="text"
            value={keyword}
            placeholder={'신도 검색'}
            onChange={e => setKeyword(e.target.value)}
          />

          <MemberHeader onClickMemberHeader={onClickMemberHeader} />

          {memberData.length !== 0 ? (
            memberData.map(member => {
              return (
                <div key={member.account_id}>
                  <MemberBox
                    clickedAccountList={clickedAccountList}
                    account_id={member.account_id}
                    birth_date={member.birth_date}
                    gender={member.gender}
                    name={member.name}
                    phone_number={member.phone_number}
                    onClickMemberBox={onClickMemberBox}
                  />
                </div>
              )
            })
          ) : (
            <EmptyText text="등록 신청을 한 신도가 없어요." />
          )}
        </MemberContainer>

        <div
          style={{
            height: '48px',
          }}
        />
        {showRejectModal && (
          <Modal
            onCloseModal={() => setShowRejectModal(false)}
            text={'신도를 거절하시겠어요?'}
            buttonText={'거절'}
            buttonColor={'#D40030'}
            clickButton={clickRejectButton}
          />
        )}
        {showSuccessModal && (
          <Modal
            onCloseModal={() => setShowSuccessModal(false)}
            text={'신도를 승인하시겠어요?'}
            buttonText={'승인'}
            buttonColor={'#5500dd'}
            clickButton={clickSuccessButton}
          />
        )}
        <Page
          pageList={pageList}
          currentPage={currentPage}
          getPrevPage={getPrevPage}
          getNextPage={getNextPage}
          clickPage={clickPage}
        />
      </DataBoxContainer>
    </Container>
  )
}
