import React, { Dispatch, SetStateAction, useEffect, useRef, useState } from "react";
import styled from "styled-components";
import Select from 'react-select'
import { setAuthToken } from "../../../../../api";

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWrapper = styled.div`
  position: relative;
  width: 700px;
  height: 600px;
  background-color: #ffffff;
  border-radius: 20px;
`;

const ModalCloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #FFFFFF;
  border: none;
  cursor: pointer;
`;

const NoticeModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const ButtonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${ButtonStyle}
`;

const DeleteButton = styled.button`
  ${ButtonStyle}
`;

const WeeklyDateSelect = styled.div`
width: 303px;
height: 56px;
`;

const Image = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const DataBox = styled.div`
  width: 100%;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;


interface PastorModalProps {
  refresh: boolean;
  setRefresh: Dispatch<SetStateAction<boolean>>;
  onCloseModal: () => void;
}

interface Pastor {
    name: string;
    role: string;
    image: string;
    uploadImage: File | undefined;
}


export const PastorCreateModal = (props: PastorModalProps) => {
  const { refresh, setRefresh ,onCloseModal } = props;

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }
  }, []);
  const [image, setImage] = useState("");
    const [uploadImage, setUploadImage] = useState<any>([]);
    const [pastorList, setPastorList] = useState<Array<Pastor>>([])
    const [totalCount, setTotalCount] = useState(1)
  const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current)
      imageFile.current.click()
  }
  const saveImageFile = (event: any) => {
    if (event.target.files !== null && event.target.files[0] !== undefined) {
      setUploadImage(event.target.files[0])
      setImage(URL.createObjectURL(event.target.files[0]))
    }
  }

  const onClickSaveButton = async() => {
    // await patchChurchWeekly(uploadImage, church_pastor_id, name, weeklyYear, weeklyMonth)
    setRefresh(!refresh)
    onCloseModal()
  }
  const onClickDeleteButton = async() => {
    // await deleteChurchWeekly(church_pastor_id)
    setRefresh(!refresh)
    onCloseModal()
  }

  return (
      <ModalOverlay >
          <ModalWrapper >
              <ModalCloseButton onClick={onCloseModal}><img src="source/icon/close.svg" alt="close" /></ModalCloseButton>
              <NoticeModalContent>

                  {pastorList && pastorList.map((pastor, index) => {
                      return (
                          <>
                        <DataBox>
            <UploadButton
                style={{
                    background: "#ffffff" ,
                    border: "1px solid #5500DD",
                    color: "#5500DD" ,
                }}
                onClick={openImageFile}
                      >
                          <input type='file' id='file' ref={imageFile} style={{ display: 'none' }} onChange={saveImageFile} />
                          업로드
                </UploadButton>
            <InputBox
                type="text"
                value={""}
                placeholder={"제목 입력"}
                // onChange={(e) => setName(e.target.value)}
            />
                    </DataBox>
                    <DataBox>
            <InputBox
                type="text"
                value={""}
                placeholder={"제목 입력"}
                // onChange={(e) => setRole(e.target.value)}
            />
                              </DataBox>
                              </>
                      )
                  })}


            <DataContainer><DataBox><AddButtonBox><AddButton onClick={() => setTotalCount(totalCount + 1)}
          style={{
            background: "#ffffff",
            border: "1px solid #5500dd",
            color: "#5500dd",
          }}
        >
          <img src="/source/icon/add.svg" alt="information" style={{ marginRight: "8px" }} />
          섬기는 이 추가
        </AddButton></AddButtonBox></DataBox></DataContainer>


          <UploadButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #5500DD",
                color: "#5500DD" ,
              }}
              onClick={onClickSaveButton}
            >
              저장
            </UploadButton>
            <DeleteButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #D40030",
                color: "#D40030" ,
              }}
              onClick={onCloseModal}
            >
              취소
          </DeleteButton>

              </NoticeModalContent>
          </ModalWrapper>
      </ModalOverlay>
  );
};
