import React, { useEffect, useState } from 'react'
import {
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  MainContainer,
  Margin36,
  SubTitle,
} from '../../../../../component/styledComponent'
import { Week, useMonth } from '../../../../../hooks/useMonth'
import { WeekView } from './week'
import styled from 'styled-components'

const WeekHeader = styled.div`
  height: 48px;
  display: flex;
  flex-direction: row;
`
const WeekHeaderText = styled.text`
  width: 128px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`

export const CalendarView = () => {
  const { weekList, year, month, setYear, setMonth } = useMonth()
  useEffect(() => {
    setMonth(2)
  }, [])

  console.log(weekList)

  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>달력</InnerBoxTitle>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <Margin36 />
        <SubTitle>
          {year}년 {month}월
        </SubTitle>
        <Margin36 />
        <WeekHeader>
          <WeekHeaderText>월</WeekHeaderText>
        </WeekHeader>
        {weekList &&
          weekList.map((week: Week[]) => {
            return <WeekView week={week} />
          })}
      </InnerBoxContainer>
    </MainContainer>
  )
}
