import React, { useEffect, useState } from 'react'
import {
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  MainContainer,
  Margin36,
  SubTitle,
} from '../../../../../component/styledComponent'
import { Date, useMonth } from '../../../../../hooks/useMonth'
import { WeekView } from './week'
import styled from 'styled-components'

const WeekHeader = styled.div`
  height: 48px;
  display: flex;
  flex-direction: row;
`
const WeekHeaderText = styled.text`
  width: 128px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`
const weekHeader = ['일', '월', '화', '수', '목', '금', '토']
export const CalendarView = () => {
  const { weekList, year, month, setYear, setMonth } = useMonth()

  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>달력</InnerBoxTitle>
        <div>
          <div onClick={() => setMonth(month - 1)}>지난달</div>
          <div onClick={() => setMonth(month + 1)}>다음달</div>
        </div>
      </InnerBoxHeader>
      <InnerBoxContainer style={{ display: 'flex', justifyContent: 'center' }}>
        <Margin36 />
        <SubTitle>
          {year}년 {month}월
        </SubTitle>
        <Margin36 />
        <WeekHeader>
          {weekHeader.map((haederText, index) => {
            return <WeekHeaderText key={index}>{haederText}</WeekHeaderText>
          })}
        </WeekHeader>
        {weekList &&
          weekList.map((week: Date[], index: number) => {
            return (
              <WeekView key={index} week={week} year={year} month={month} />
            )
          })}
      </InnerBoxContainer>
    </MainContainer>
  )
}
