import { instance } from "..";

export const getNotice = async (offset:number) => {
    try {
        const response = await instance.get("administrators/churchs/notice/", { params: { offset } }
        );
        return response.data;
    } catch (error) {
        console.error(error);
    }
};

export const createChurchNotification = async (image: any, title: string | undefined, content: string | undefined) => {
    console.log(image, title, content)
    const formData = new FormData();
    image.length !== 0 && formData.append('image', image, image.name);
    title!== undefined && formData.append('title', title);
    content!== undefined && formData.append('content', content);
  try {
    const response = await instance.post("administrators/churchs/notice/",formData);
    return response.data;
  } catch (error) {
    console.error(error);
  }
};

export const patchChurchNotification = async (image:any, title:string|undefined, content:string|undefined) => {
    const formData = new FormData();
    image.length !== 0 && typeof image[0] === typeof true ? formData.append('is_delete_image', "false") : formData.append('image', image, image.name);
    title!== undefined && formData.append('title', title);
    content!== undefined && formData.append('content', content);

  try {
    const response = await instance.post("administrators/churchs/notice/",formData);
    return response.data;
  } catch (error) {
    console.error(error);
  }
};
