import styled from 'styled-components'

export const MainContainer = styled.div`
  width: 100%;
  background: #f0f1f3;
`

export const InnerBoxHeader = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 36px;
  margin-right: 36px;
  padding-top: 24px;
`

export const InnerBoxTitle = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 40px;
  color: #000000;
`
export const SubTitle = styled.div`
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #000000;
`

export const InnerBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 36px;
  margin-right: 36px;
  width: calc(100% - 144px);
  min-width: 812px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
  padding-left: 36px;
  padding-right: 36px;
`

export const Margin36 = styled.div`
  padding-top: 36px;
`
export const Margin16 = styled.div`
  padding-top: 16px;
`

export const FlexRowContainer = styled.div`
  display: flex;
  flex-direction: row;
`

export const FlexRowInnerLeft = styled.div`
  width: 50%;
  margin-right: 36px;
`
export const FlexRowInnerRight = styled.div`
  width: 50%;
`

export const InnerBox = styled.div`
  width: 100%;
  border-radius: 16px;
  border: 1px solid #c1c2c3;
  display: flex;
  flex-direction: row;
  align-items: center;
`

export const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
`

export const FocusedInputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #000000;
  padding: 0 78px 0 16px;
  color: #000000;
`
