import { Dispatch, SetStateAction, useRef, useState } from "react";
import styled from "styled-components";

const Image = styled.div`
  margin-top: 24px;
  width: 40px;
  height: 60px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

interface Pastor {
    name: string;
    role: string;
    image: string;
    uploadImage: File | undefined;
    imageFile: any;
}

interface PastorImageProps {
    index: number;
    saveImageFile: Function;
    image: string;
}

export const PastorImage = (props: PastorImageProps) => {
    const {index, saveImageFile, image} = props
    const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current)
      imageFile.current.click()
  }


    return (
        <Image
    style={{
        background: "#ffffff" ,
        border: "1px solid #5500DD",
        color: "#5500DD" ,
    }}
    onClick={openImageFile}
            >
        <input type='file' id='file' ref={imageFile} style={{ display: 'none' }} onChange={(e) => saveImageFile(e,index)} />
        <img src={image} />
    </Image>
    )
}
