import React, { useEffect, useState } from 'react'
import { Date } from '../../../../../hooks/useMonth'
import styled from 'styled-components'
import { useWeek } from '../../../../../hooks/useWeek'

const DayContainer = styled.div`
  width: 128px;
  height: 26px;
`

const WeekDateContainer = styled.div`
  height: 30px;
  display: flex;
  flex-direction: row;
`
const WeekContentContainer = styled.div`
  height: 94px;
`
const DateText = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`

const ScheduleBox = styled.div`
  margin-top: 2px;
  border-radius: 8px;
  background: #5500dd;
  position: absolute;
  width: 124px;
  height: 26px;
  text-align: left;
  justify-content: center;
`
const ScheduleTitle = styled.text`
  padding-bottom: 2px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 22px;
  text-align: left;
`
interface DayProps {
  index: number
  date: Date
  currentYear: number
  currentMonth: number
}
interface WeekViewProps {
  week: Array<Date>
  year: number
  month: number
}

const test_calendar = [
  {
    church_calendar_id: '1',
    title: 'title1',
    content: 'content2',
    start_date: '2023-02-28',
    end_date: '',
    is_current_month: false,
  },
  {
    church_calendar_id: '2',
    title: 'title2',
    content: 'content2',
    start_date: '2023-03-01',
    end_date: '2023-03-05',
    is_current_month: true,
  },
  {
    church_calendar_id: '3',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '2023-03-01',
    is_current_month: true,
  },
]

const Day = (props: DayProps) => {
  const { index, date, currentYear, currentMonth } = props
  return (
    <DayContainer>
      <DateText
        style={{
          color:
            date.month !== currentMonth
              ? '#c1c2c3'
              : index === 0
              ? '#D40030'
              : '#000000',
        }}
      >
        {date.date}
      </DateText>
    </DayContainer>
  )
}

export const WeekView = (props: WeekViewProps) => {
  const { week, year, month } = props
  const { weekData } = useWeek(week, test_calendar)
  console.log('weekSchedule', weekData)
  return (
    <>
      <WeekDateContainer>
        {week &&
          week.map((day, index) => {
            return (
              <Day
                key={index}
                index={index}
                date={day}
                currentYear={year}
                currentMonth={month}
              />
            )
          })}
      </WeekDateContainer>

      <WeekContentContainer>
        {weekData.length !== 0 &&
          weekData.map(schedule => {
            return (
              <ScheduleBox>
                <ScheduleTitle>{schedule.title}</ScheduleTitle>
              </ScheduleBox>
            )
          })}

        <ScheduleBox style={{ marginLeft: '130px' }}></ScheduleBox>
        <ScheduleBox>123</ScheduleBox>
      </WeekContentContainer>
    </>
  )
}
