import React, { Dispatch, SetStateAction, useEffect } from "react";
import styled from "styled-components";
import { useState, useRef } from "react";
import { PastorInfoModal } from "./patch-modal/info/modal";
import { usePage } from "../../../../hooks/usePage";
import { setAuthToken } from "../../../../api";
import { getPastor } from "../../../../api/admin-pastor";
import { PastorCreateModal } from "./create-modal/modal";

declare const daum: any;

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`;

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`;

const MainTitleText = styled.div`
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`;

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${buttonStyle}
`;

const DeleteButton = styled.button`
  ${buttonStyle}
`;

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`;

const DataBox = styled.div`
  width: 100%;
`;

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`;

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`;

const LogoImage = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const InputBoxWithCount = styled.div`
  position: relative;
`;

const WeeklyImage = styled.div`
  width: 58px;
  height: 78px;
  border-radius: 9px;
  background: #000000;
  margin-left: 24px;
  margin-top: 16px;
  overflow: hidden;
