interface PastorData {
  church_pastor_id: string;
  order: string;
  name: string;
  role: string;
  image: string;
}
const pagePerLimit = 6
export const InnerPastorPage = () => {
  const [refresh, setRefresh] = useState<boolean>(false)
  const [pastorData, setPastorData] = useState<Array<PastorData>>([{
    church_pastor_id: "",
    order: "",
    name: "",
    role: "",
    image: "",
  }]);
  const [pastorPage, setPastorPage] = useState(0);
  const [currentIndex, setIndex] = useState(0)

  const {pageList, currentPage, getPrevPage, getNextPage, clickPage} = usePage({maxPage: pastorPage})

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }

    const fetchData = async () => {
      const response = await getPastor((currentPage - 1) * pagePerLimit);
      setPastorData(response.payload.church_pastor_list.results);
      setPastorPage(Math.ceil(response.payload.church_pastor_list.total_count / pagePerLimit))
      console.log(response.payload.church_pastor_list.total_count)
    };
    fetchData();
  }, [refresh]);

  const getWeeklyPage = async (page: number) => {
    const response = await getPastor((page-1) * pagePerLimit);
    setPastorData(response.payload.church_weekly_list.results);
  }

  useEffect(() => {
    getWeeklyPage(currentPage)
  },[currentPage])


  // Modal
  const [showPatchInfoModal, setShowPatchInfoModal] = useState(false);
  const [showCreateModal, setShowCreateInfoModal] = useState(false);

  const handleButtonClick = (index:number) => {
    setShowPatchInfoModal(true);
    setIndex(index)
  };

  const handleCloseModal = () => {
    setShowPatchInfoModal(false);
  };

  const handleCreateButtonClick = (index:number) => {
    setShowCreateInfoModal(true);
  };
  const handleCloseCreateModal = () => {
    setShowCreateInfoModal(true);
  };

  return (
    <Container>
      <Header>
        <MainTitleText>섬기는 이</MainTitleText>
        <ModifyButtonBox>
          <ModifyButton
            style={{
              background: "#ffffff",
              border: "1px solid #5500dd",
              color: "#5500dd",
            }}
          >
            순서 변경
          </ModifyButton>
          <ModifyButton
            style={{
              background: "#ffffff",
              border: "1px solid #5500dd",
              color: "#5500dd",
            }}
          >
            수정
          </ModifyButton>
        </ModifyButtonBox>
      </Header>
      <DataBoxContainer>
        <DataContainer><DataBox><InfoBox>
          <img src="/source/icon/information.svg" alt="information" style={{ marginTop: "-24px" }} />
          <InfoTitle>화면에 보이는 순서대로 어플리케이션에 보여져요.<br />만약 순서를 변경하고 싶다면 <BodyTitleBold>[순서 변경]</BodyTitleBold>에서 순서를 변경해주세요.</InfoTitle>
        </InfoBox></DataBox>
        </DataContainer>
        {pastorData && pastorData.map((pastor,index) => {
          return <NoticeBox onClick={() => handleButtonClick(index)}>
                <WeeklyImage><img src={pastor.image} alt="" /></WeeklyImage>
                <NoticeTitle>{pastor.name}</NoticeTitle>
                <NoticeSubTitle>{pastor.role}</NoticeSubTitle>
              </NoticeBox>
            })}
        <DataContainer><DataBox><AddButtonBox><AddButton onClick={() => handleCreateButtonClick}
          style={{
            background: "#ffffff",
            border: "1px solid #5500dd",
            color: "#5500dd",
          }}
        >
          <img src="/source/icon/add.svg" alt="information" style={{ marginRight: "8px" }} />
          섬기는 이 추가
        </AddButton></AddButtonBox></DataBox></DataContainer>
        <img onClick={getPrevPage} src="/source/icon/arrow_left_regular.svg" alt="information" />
        {pageList.map((page) => {
          if (page === currentPage) { return <CurrentPageText>{page}</CurrentPageText> }
          else { return <PageText onClick={() => clickPage(page)}>{page}</PageText>}
        })}
        <img onClick={getNextPage} src="/source/icon/arrow_right_regular.svg" alt="information" />
        {showPatchInfoModal &&
          <PastorInfoModal
          refresh = {refresh}
          setRefresh={setRefresh}
          onCloseModal={handleCloseModal}
          church_pastor_id={pastorData[currentIndex].church_pastor_id}
          pastor_index={currentIndex + ((currentPage - 1) * pagePerLimit)}
          pastor_name={pastorData[currentIndex].name}
          pastor_role={pastorData[currentIndex].role}
          pastor_image={pastorData[currentIndex].image}
          />}
        {showCreateModal && <PastorCreateModal
          refresh = {refresh}
          setRefresh={setRefresh}
          onCloseModal={handleCloseModal}
        />}
        <div
          style={{
            height: "48px",
          }}
        />
      </DataBoxContainer>
      <div
        style={{
          height: "48px",
        }}
      />
    </Container >
  );
};
