import React, { useEffect, useState } from "react";
import styled from "styled-components";
import InfiniteScroll from 'react-infinite-scroll-component';
import { setAuthToken } from "../../../../../../api";
import { getPastor } from "../../../../../../api/admin-pastor";

const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #F0F1F3;
`;

interface PastorData {
    church_pastor_id: string;
    name: string;
    role: string;
    image: string;
}

export const PatchModal: React.FC = () => {
  const [offset, setOffset] = useState(0)
  // const [totalCount, setTotalCount] = useState(0)
  const [pastors, setPastors] = useState<Array<PastorData>>([{
      church_pastor_id:"",
      name:"",
      role:"",
      image:"",
  }])

  const loadMoreItem = async () => {
    setOffset(offset + 15)
    const response = await getPastor(offset + 15);
    setPastors([...pastors,response.payload.church_pastor_list.results]);
  }

  useEffect(() => {
      const token = localStorage.getItem("token");
      if (token) {
        setAuthToken(token);
      }

      const fetchData = async () => {
        const response = await getPastor(0);
        setPastors(response.payload.church_pastor_list.results);
        console.log(response.payload.church_pastor_list.results)
      //   setTotalCount(Math.floor(response.payload.church_weekly_list.total_count / 7 + 1))
      };
      fetchData();
  }, []);

  // console.log(pastors)

  return (
    <Container>
      <div style={{ background: "#F0F1F3", paddingTop: "54px" }}></div>
      <InfiniteScroll
        dataLength={pastors.length} //This is important field to render the next data
        next={loadMoreItem}
        hasMore={true}
        loader={<h4>Loading...</h4>}
        endMessage={
          <p style={{ textAlign: 'center' }}>
            <b>Yay! You have seen it all</b>
          </p>
        }
        // below props only if you need pull down functionality
        // refreshFunction={refresh}
        // pullDownToRefresh
        pullDownToRefreshThreshold={50}
        pullDownToRefreshContent={
          <h3 style={{ textAlign: 'center' }}>&#8595; Pull down to refresh</h3>
        }
        releaseToRefreshContent={
          <h3 style={{ textAlign: 'center' }}>&#8593; Release to refresh</h3>
        }
          >
          {pastors && pastors.map((pastor) => { return (<div>{pastor.name}</div>) })}
      </InfiniteScroll>
    </Container>
  );
};
