import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

const Check = styled.image`
  width: 24px;
  height: 24px;
  margin-left: 24px;
  margin-right: 24px;
`

const Text = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`
const SmallBox = styled.div`
  width: 130px;
  height: 100%;
`
interface MemberBoxProps {
  account_id: string
  birth_date: string
  gender: string
  name: string
  phone_number: string
  onClickMemberBox: (account_id: string) => void
}

export const MemberBox = (props: MemberBoxProps) => {
  const {
    account_id,
    birth_date,
    gender,
    name,
    phone_number,
    onClickMemberBox,
  } = props
  const [clicked, setClicked] = useState(false)
  const clickMember = () => {
    onClickMemberBox(account_id)
    setClicked(!clicked)
  }
  return (
    <div
      style={{
        background: clicked ? '#F0F1F3' : '#ffffff',
        display: 'flex',
        width: '100%',
        marginTop: '8px',
        height: '56px',
        alignItems: 'center',
      }}
      onClick={clickMember}
    >
      <Check>
        <img
          src={clicked ? '/source/icon/check.svg' : '/source/icon/no_check.svg'}
          alt=""
        />
      </Check>
      <Text>{name}</Text>
      <Text>{gender}</Text>
      <Text>{birth_date}</Text>
      <Text>{phone_number}</Text>
    </div>
  )
}
