import React, { Dispatch, SetStateAction, useRef } from 'react'
import styled from 'styled-components'
import { useState } from 'react'
import { createChurchNotification } from '../../../../api/admin-notification'
import {
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  MainContainer,
} from '../../../../component/styledComponent'

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`

const SubTitleText = styled.div`
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`

const SubTitleColorText = styled.text`
  color: #5500dd;
`

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`

interface ModifyButtonProps {
  active: boolean
}

const ModifyButton = styled.button<ModifyButtonProps>`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
  background: ${({ active }) => (active ? '#5500DD' : '#ffffff')};
  border: ${({ active }) => (active ? 'none' : '1px solid #5500DD')};
  color: ${({ active }) => (active ? '#ffffff' : '#5500DD')};
`

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500dd;
  text-align: center;
  cursor: pointer;
`

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`

const UploadButton = styled.button`
  ${buttonStyle}
`

const DeleteButton = styled.button`
  ${buttonStyle}
`

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`

const ErrorText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 22px;
  color: #d40030;
  text-align: left;
  margin-top: 8px;
  margin-left: 16px;
`

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`

const DataBox = styled.div`
  width: 100%;
`

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`

const NotificationImage = styled.div`
  margin-top: 24px;
  width: 372px;
  height: 200px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`

const InputBoxWithCount = styled.div`
  position: relative;
`

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`

interface InputBoxProps {
  error: boolean
  value: string
}
const InputBox = styled.input<InputBoxProps>`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
