import { Dispatch, SetStateAction, useRef, useState } from 'react'
import styled from 'styled-components'

const Container = styled.div`
  height: 110px;
  display: flex;
  flex-direction: row;
  align-items: center;
  border: 1px solid #c1c2c3;
  border-radius: 16px;
  padding-left: 24px;
  margin-bottom: 16px;
  margin-left: 36px;
  margin-right: 36px;
`
const IndexIndicator = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 20px;
  line-height: 28px;
  color: #5500dd;
`
const Image = styled.div`
  width: 58px;
  height: 78px;
  background: #f0f1f3;
  border-radius: 16px;
  border: none;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  margin-left: 24px;
  margin-right: 24px;
`
const InputBox = styled.input`
  width: 226px;
  box-sizing: border-box;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
  margin-right: 24px;
`

interface PastorImageProps {
  index: number
  image: string
  name: string
  role: string
  changeIndex: Function
}

export const PastorBoxOrder = (props: PastorImageProps) => {
  const { index, image, name, role, changeIndex } = props

  return (
    <Container>
      <InputBox
        type="text"
        value={index + 1}
        placeholder={'제목 입력'}
        onChange={e => changeIndex(index, e.target.value)}
      />
      <IndexIndicator>{index + 1}</IndexIndicator>
      <Image
        style={{
          background: '#ffffff',
        }}
      >
        <img
          src={image === '' ? '/source/icon/pastor_upload_image.svg' : image}
        />
      </Image>
      {name}
      {role}
    </Container>
  )
}
