import React from 'react'
import styled from 'styled-components'
import { Header } from '../main-pages/header'
import { Margin36 } from '../component/styledComponent'
import { TermBox } from './termBox'

const Container = styled.div`
  width: 100%;
`
const Title = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  color: #000000;
`
const BodyContainer = styled.div`
  margin: 0 auto;
  width: 1280px;
  height: 800px;
`

export const Location = () => {
  return (
    <Container>
      <Header />
      <Margin36 />
      <BodyContainer>
        <TermBox
          title="제1조(목적)"
          contents={[
            '본 약관은 서비스 이용약관에 동의한 이용자(이하 "회원" 또는 "사용자")가 업텐(이하 "회사")에서 제공하는 주다 서비스(이하 "서비스"라 하며, 추후 서비스명은 변경될 수 있음)를 이용함에 있어 회사와 회원 간 또는 회원 간의 권리, 의무 및 책임사항 및 서비스 이용조건 및 절차 등 기본적인 사항을 규정함을 목적으로 합니다. 회사가 서비스 이용약관을 개정하는 경우에는 홈페이지 또는 애플리케이션에 게시하거나 개별적으로 공지할 것입니다.',
          ]}
        />
        <TermBox
          title="제2조(제공 서비스)"
          contents={[
            '회사가 제공하는 서비스는 다음과 같습니다.',
            '•  서비스명 : 주다(jooda)',
            '•  서비스 내용 : 교회 통합 정보 플랫폼',
          ]}
        />
        <TermBox
          title="제3조(이용자의 주의사항)"
          contents={[
            '회사는 이용자가 아래와 같이 잘못된 방법이나 행위로 서비스를 이용할 경우 사용에 대한 제재(이용정지, 강제 탈퇴 등)를 가할 수 있습니다.',
            '1. 잘못된 방법으로 서비스의 제공을 방해하거나 회사가 안내하는 방법 이외의 다른 방법을 사용하여 서비스에 접근하는 행위',
            '2. 다른 이용자의 정보를 무단으로 수집, 이용하거나 다른 사람들에게 제공하는 행위',
            '3. 서비스를 영리나 홍보 목적으로 이용하는 행위',
            '4. 음란 정보나 저작권 침해 정보 등 공서양속 및 법령에 위반되는 내용의 정보 등을 발송하거나 게시하는 행위',
            '5. 회사의 동의 없이 서비스 또는 이에 포함된 소프트웨어 일부를 복사, 수정, 배포, 판매, 양도, 대여, 담보 제공하거나 타인에게 그 이용을 허락하는 행위',
            '6. 소프트웨어를 역설계하거나 소스 코드의 추출을 시도하는 등 회사의 서비스를 복제, 분해 또는 모방하거나 기타 변형하는 행위',
            '7. 관련 법령 또는 회사의 모든 약관을 준수하지 않는 행위',
          ]}
        />
        <TermBox
          title="제4조(게시물의 관리)"
          contents={[
            '1. 이용자의 게시물이 정보통신망법 및 저작권법 등 관련 법에 위반되는 내용을 포함하는 경우, 권리자는 관련 법이 정한 절차에 따라 해당 게시물의 게시 중단 및 삭제 등을 요청할 수 있으며, 회사는 관련 법에 따라 조치를 취하여야 합니다.',
            '2. 회사는 전항에 따른 권리자의 요청이 없는 경우라도 권리침해가 인정될 만한 사유가 있거나 본 약관 및 기타 회사 정책, 관련 법에 위반되는 경우에는 관련 법에 따라 해당 게시물에 대해 임시 조치 등을 취할 수 있습니다.',
          ]}
        />
      </BodyContainer>
    </Container>
  )
}
