import React, { useEffect, useState } from 'react'
import { Week } from '../../../../../hooks/useMonth'
import styled from 'styled-components'

const DayContainer = styled.div`
  width: 128px;
  height: 124px;
`
const WeekContainer = styled.div`
  height: 124px;
  display: flex;
  flex-direction: row;
`
interface WeekViewProps {
  week: Array<Week>
}

const Day = (props: Week) => {
  const { date, isCurrentMonth } = props
  return (
    <DayContainer>
      {date}
      {'  '}
      {isCurrentMonth}
    </DayContainer>
  )
}

export const WeekView = (props: WeekViewProps) => {
  const { week } = props

  console.log(week)

  return (
    <WeekContainer>
      {week &&
        week.map(day => {
          return <Day date={day.date} isCurrentMonth={day.isCurrentMonth} />
        })}
    </WeekContainer>
  )
}
