import React from "react";
import styled from "styled-components";
import { useState, useRef } from "react";

declare const daum: any;

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`;

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`;

const MainTitleText = styled.div`
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`;

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${buttonStyle}
`;

const DeleteButton = styled.button`
  ${buttonStyle}
`;

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`;

const DataBox = styled.div`
  width: 100%;
`;

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`;

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`;

const NotificationImage = styled.div`
  margin-top: 24px;
  width: 372px;
  height: 200px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const InputBoxWithCount = styled.div`
  position: relative;
`;

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
`;

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 148px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  resize: none;
  overflow: auto;
`;

const BodyTitleBold = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  color: #454648;
`;

export const InnerRegistPage: React.FC = () => {
  const [editState, setEditState] = useState(false);
  const [notificationTitle, setNotoficationTitle] = useState("");
  const [notificationContent, setNotificationContent] = useState("");

  const [notificationTitleCount, setIntroductionTitleCount] = useState(0);
  const [notificationContentCount, setNotificationContentCount] = useState(0);

  const handleEdit = () => {
    if (editState) {
      setEditState(false);
    } else {
      setEditState(true);
    }
  };

  return (
    <Container>
      <Header>
        <MainTitleText>공지사항 등록</MainTitleText>
        <ModifyButtonBox>
          <ModifyButton
            onClick={handleEdit}
            style={{
              background: editState ? "#5500DD" : "#ffffff",
              border: editState ? "none" : "1px solid #5500dd",
              color: editState ? "#ffffff" : "#5500dd",
            }}
          >
            {editState ? "저장" : "등록"}
          </ModifyButton>
        </ModifyButtonBox>
      </Header>
      <DataBoxContainer>
        <DataContainer><DataBox>
          <SubTitleText>제목</SubTitleText>
          <InputBoxWithCount>
            <InputBox
              type="text"
              maxLength={20}
              value={notificationTitle}
              disabled={!editState}
              placeholder="제목 입력"
              onChange={(e) => {
                setNotoficationTitle(e.target.value);
                setIntroductionTitleCount(e.target.value.length);
              }}
            />
            <InputCount>({notificationTitleCount}/20)</InputCount>
          </InputBoxWithCount>
        </DataBox>
        </DataContainer>
        <DataContainer>
          <DataBox>
            <SubTitleText>내용</SubTitleText>
            <InputBoxWithCount>
              <InputSubBox
                value={notificationContent}
                disabled={!editState}
                placeholder="내용 입력"
                onChange={(e) => {
                  setNotificationContent(e.target.value);
                  setNotificationContentCount(e.target.value.length);
                }}
              />
              <InputCount>({notificationContentCount})</InputCount>
            </InputBoxWithCount>
          </DataBox>
        </DataContainer>
        <DataContainer>
          <DataBox>
            <SubTitleText>이미지</SubTitleText>
            <InfoBox>
              <img src="/source/icon/information.svg" alt="information" />
              <InfoTitle>글과 함께 볼 수 있는 이미지가 있다면 등록해주세요.</InfoTitle>
            </InfoBox>
            <NotificationImage></NotificationImage>
            <ButtonBox>
              <UploadButton
                style={{
                  background: editState ? "#ffffff" : "#E3E4E6",
                  border: editState ? "1px solid #5500DD" : "none",
                  color: editState ? "#5500DD" : "#A2A3A5",
                }}
              >
                업로드
              </UploadButton>
              <DeleteButton
                style={{
                  background: editState ? "#ffffff" : "#E3E4E6",
                  border: editState ? "1px solid #D40030" : "none",
