import React from 'react'
import styled from 'styled-components'
import { Header } from './header'
import { Footer } from './footer'
import {
  Animator,
  ScrollContainer,
  ScrollPage,
  batch,
  Fade,
  FadeIn,
  FadeOut,
  Move,
  MoveIn,
  MoveOut,
  Sticky,
  StickyIn,
  StickyOut,
  Zoom,
  ZoomIn,
  ZoomOut,
} from 'react-scroll-motion'
import { Page1 } from './home-pages/page1'
import { Page3 } from './home-pages/page3'

const Container = styled.div`
  width: 100%;
`

const BodyContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 0 auto;
  width: 1280px;
  background-color: #f5f4f7;
`

const Body = styled.div`
  margin-left: 100px;
  text-align: left;
`

const BodyImg = styled.div`
  margin-right: 100px;
  display: flex;
  align-items: flex-end;
`

const BodyTitle = styled.div`
  margin-top: 260px;
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 48px;
  line-height: 60px;
  color: #000000;
`

const BodyTitleBold = styled.text`
  ffont-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 48px;
  line-height: 60px;
`

const BodySubTitle = styled.div`
  margin-top: 24px;
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 24px;
  line-height: 32px;
  color: #797a7b;
`

const SubButton = styled.button`
  margin-top: 64px;
  width: 168px;
  height: 48px;
  background: #5500dd;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  color: #ffffff;
  border: none;
`
const ZoomInScrollOut = batch(StickyIn(), FadeIn(), ZoomIn())
const FadeUp = batch(Fade(), Move(), Sticky())
export const HomePage: React.FC = () => {
  return (
    <Container>
      <Header />
      <BodyContainer>
        <ScrollContainer>
          <ScrollPage>
            <Page1 />
          </ScrollPage>
          <ScrollPage>
            <Animator animation={ZoomInScrollOut}>
              <span style={{ fontSize: '40px' }}>I'm FadeUpScrollOut ✨</span>
            </Animator>
          </ScrollPage>
          <ScrollPage>
            <Page3 />
          </ScrollPage>
          <ScrollPage>
            <div
              style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100%',
              }}
            >
              <span style={{ fontSize: '40px' }}>
                <Animator animation={MoveIn(-1000, 0)}>Hello Guys 👋🏻</Animator>
                <Animator animation={MoveIn(1000, 0)}>
                  Nice to meet you 🙋🏻‍♀️
                </Animator>
                - I'm Dante Chun -
                <Animator animation={MoveOut(1000, 0)}>Good bye ✋🏻</Animator>
                <Animator animation={MoveOut(-1000, 0)}>See you 💛</Animator>
              </span>
            </div>
          </ScrollPage>
          <ScrollPage>
            <Animator animation={batch(Fade(), Sticky())}>
              <span style={{ fontSize: '40px' }}>Done</span>
              <br />
              <span style={{ fontSize: '30px' }}>
                There's FadeAnimation, MoveAnimation, StickyAnimation,
                ZoomAnimation
              </span>
            </Animator>
          </ScrollPage>
        </ScrollContainer>
      </BodyContainer>
      <Footer />
    </Container>
  )
}
