import React, { Dispatch, SetStateAction, useEffect, useState } from 'react'
import {
  InnerBox,
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  MainContainer,
  Margin36,
} from '../../../../../component/styledComponent'
import { InfoBox } from '../../../../../component/box/info'
import { usePage } from '../../../../../hooks/usePage'
import { Page } from '../../../../../component/page'
import { getSchedule } from '../../../../../api/admin-schedule'
import { setAuthToken } from '../../../../../api'
import { EmptyText } from '../../../../../component/empty_text'
interface ScheduleBoxProps {
  index: number
  title: string
  subtitle: string
  time: string
}
const ScheduleBox = (props: ScheduleBoxProps) => {
  const { index, title, subtitle, time } = props
  return (
    <InnerBox>
      {index}
      {title}
      {subtitle}
    </InnerBox>
  )
}
interface Schedule {
  church_worship_schedule_id: string
  title: string
  subtitle: string
  weekday: string
  place: string
  mc: string
  target: string
  reference: string
  time: string
}
interface InnerScheduleProps {
  refresh: boolean
  setRefresh: Dispatch<SetStateAction<boolean>>
}

const pagePerLimit = 6
export const InnerScheduleList = (props: InnerScheduleProps) => {
  const { refresh, setRefresh } = props
  const {
    pageList,
    currentPage,
    maxPage,
    getPrevPage,
    getNextPage,
    clickPage,
    setMaxPage,
  } = usePage()
  const [scheduleData, setScheduleData] = useState<Array<Schedule>>([])

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    const fetchData = async () => {
      const response = await getSchedule()
      setScheduleData(response.payload.worship_schedule_list)
    }

    fetchData()
  }, [refresh])

  useEffect(() => {
    setMaxPage(Math.ceil(scheduleData.length / pagePerLimit))
  }, [scheduleData])

  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>예배 일정 목록</InnerBoxTitle>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <Margin36 />
        <InfoBox text="박스를 클릭하면 수정 및 삭제를 할 수 있어요." />
        {scheduleData.length !== 0 && <Margin36 />}
        {scheduleData.length !== 0 ? (
          scheduleData.map((schedule, index) => {
            return (
              <ScheduleBox
                index={index}
                title={schedule.title}
                subtitle={schedule.subtitle}
                time={schedule.time}
              />
            )
          })
        ) : (
          <EmptyText text="등록된 예배 일정이 없어요." />
        )}
        {/* <ScheduleBox index={0} /> */}
        <Page
          pageList={pageList}
          currentPage={currentPage}
          getPrevPage={getPrevPage}
          getNextPage={getNextPage}
          clickPage={clickPage}
          lastPage={maxPage}
        />
      </InnerBoxContainer>
    </MainContainer>
  )
}
