import React from "react";
import styled from "styled-components";
import { useState, useRef } from "react";

declare const daum: any;

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`;

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`;

const MainTitleText = styled.div`
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`;

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${buttonStyle}
`;

const DeleteButton = styled.button`
  ${buttonStyle}
`;

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`;

const DataBox = styled.div`
  width: 100%;
`;

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`;

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`;

const LogoImage = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const InputBoxWithCount = styled.div`
  position: relative;
`;

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
`;

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 148px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  resize: none;
  overflow: auto;
`;

const BodyTitleBold = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  color: #454648;
`;

export const InnerPastorPage: React.FC = () => {
  const [editState, setEditState] = useState(false);
  const [logo, setLogo] = useState("");
  const [thumbnail, setThumbnail] = useState("");
  const [churchName, setChruchName] = useState("");
  const [denominationName, setDenominationName] = useState("");
  const [contackNumber, setContackNumber] = useState("");
  const [introductionTitle, setIntroductionTitle] = useState("");
  const [introductionContent, setIntroductionContent] = useState("");
  const [address, setAddress] = useState("");
  const [detailAddress, setDetailAddress] = useState("");

  const [detailAddressCount, setDetailAddressCount] = useState(0);
  const [introductionTitleCount, setIntroductionTitleCount] = useState(0);
  const [introductionContentCount, setIntroductionContentCount] = useState(0);

  const handleEdit = () => {
    if (editState) {
      setEditState(false);
    } else {
      setEditState(true);
    }
  };

  const addressRef = useRef<HTMLInputElement>(null);

  const handleAddressSearch = () => {
    if (!editState) {
      return;
    }
    const popupWidth = 500;
    const popupHeight = 600;
    const windowWidth =
      window.innerWidth ||
      document.documentElement.clientWidth ||
      document.body.clientWidth;
    const windowHeight =
      window.innerHeight ||
      document.documentElement.clientHeight ||
      document.body.clientHeight;

    const popupLeft = (windowWidth - popupWidth) / 2;
    const popupTop = (windowHeight - popupHeight) / 2;

    new daum.Postcode({
      left: popupLeft,
      top: popupTop,
      width: "100%",
      height: "100%",
      oncomplete: function (data: any) {
        setAddress(data.roadAddress);
        if (addressRef.current) {
          addressRef.current.focus();
        }
      },
    }).open();
  };

  return (
    <Container>
      <Header>
        <MainTitleText>섬기는 이</MainTitleText>
        <ModifyButtonBox>
          <ModifyButton
            onClick={handleEdit}
            style={{
              background: "#ffffff",
              border: "1px solid #5500dd",
              color: "#5500dd",
            }}
          >
            순서 변경
          </ModifyButton>
          <ModifyButton
            onClick={handleEdit}
            style={{
              background: "#ffffff",
              border: "1px solid #5500dd",
              color: "#5500dd",
            }}
          >
            수정
          </ModifyButton>
        </ModifyButtonBox>
      </Header>
      <DataBoxContainer>
        <DataContainer><DataBox><InfoBox>
          <img src="/source/icon/information.svg" alt="information" style={{ marginTop: "-24px" }} />
          <InfoTitle>화면에 보이는 순서대로 어플리케이션에 보여져요.<br />만약 순서를 변경하고 싶다면 <BodyTitleBold>[순서 변경]</BodyTitleBold>에서 순서를 변경해주세요.</InfoTitle>
        </InfoBox></DataBox>
        </DataContainer>
        <DataContainer><DataBox><AddButtonBox><AddButton
          // onClick={handleEdit}
          style={{
            background: "#ffffff",
            border: "1px solid #5500dd",
            color: "#5500dd",
          }}
        >
          <img src="/source/icon/add.svg" alt="information" style={{ marginRight: "8px" }} />
          섬기는 이 추가
        </AddButton></AddButtonBox></DataBox></DataContainer>
        <div
          style={{
            height: "48px",
          }}
        />
      </DataBoxContainer>
      <div
        style={{
          height: "48px",
        }}
      />
    </Container >
  );
};
