import { useCallback, useEffect, useRef, useState } from 'react'

export const useMonth = () => {
  const date = new Date()

  const [year, setYear] = useState(date.getFullYear())
  const [month, setMonth] = useState(date.getMonth() + 1)
  let weekList: number[][] = []

  const getWeekList = useCallback(() => {
    var firstDay = new Date(year, month - 1, 1).getDay()
    var lastDay = new Date(year, month, 0).getDate()
    var beforeMonthLastDay = new Date(year, month - 1, 0).getDate()
    var weekCount = Math.ceil((lastDay + firstDay) / 7)
    var lastDate = 0
    for (let i = 0; i < weekCount; i++) {
      var week = []
      if (i === 0) {
        for (let j = firstDay - 1; j >= 0; j--) {
          week.push(beforeMonthLastDay - j)
        }
        for (let j = 1; j <= 7 - firstDay; j++) {
          week.push(j)
        }
      }
      weeklist[i] = week
      console.log(weekList)
    }
  }, [year, month])

  useEffect(() => {
    getWeekList()
  }, [year, month])

  return { weekList, year, month, setYear, setMonth }
}
