import React from 'react'
import styled from 'styled-components'
const CurrentPageText = styled.button`
  background: #5500dd;
  color: #ffffff;
  border: 0px;
  margin-bottom: 16px;
`

const PageText = styled.button`
  background: #ffffff;
  color: #c1c2c3;
  border: 0px;
  margin-bottom: 16px;
`

interface PageProps {
  pageList: Array<number>
  currentPage: number
  getPrevPage: Function
  getNextPage: Function
  clickPage: Function
}

export const Page = (props: PageProps) => {
  const { getPrevPage, getNextPage, clickPage, pageList, currentPage } = props
  return (
    <>
      <img
        onClick={() => getPrevPage()}
        src="/source/icon/arrow_left_regular.svg"
        alt="information"
      />
      {pageList.map(page => {
        if (page === currentPage) {
          return <CurrentPageText>{page}</CurrentPageText>
        } else {
          return <PageText onClick={() => clickPage(page)}>{page}</PageText>
        }
      })}
      <img
        onClick={() => getNextPage()}
        src="/source/icon/arrow_right_regular.svg"
        alt="information"
      />
    </>
  )
}
