import { Dispatch, SetStateAction, useRef, useState } from 'react'
import styled from 'styled-components'

const Image = styled.div`
  width: 58px;
  height: 78px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`

interface PastorImageProps {
  index: number
  saveImageFile: Function
  image: string
}

export const PastorImage = (props: PastorImageProps) => {
  const { index, saveImageFile, image } = props
  const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current) imageFile.current.click()
  }

  return (
    <Image
      style={{
        background: '#ffffff',
        border: '1px solid #5500DD',
        color: '#5500DD',
      }}
      onClick={openImageFile}
    >
      <input
        type="file"
        id="file"
        ref={imageFile}
        style={{ display: 'none' }}
        onChange={e => saveImageFile(e, index)}
      />
      <img
        src={
          image.length === 0 ? '/source/icon/pastor_upload_image.svg' : image
        }
      />
    </Image>
  )
}
