import React, { Dispatch, SetStateAction, useEffect } from "react";
import styled from "styled-components";
import { useState, useRef } from "react";
import { setAuthToken } from "../../../../api";
import { getNotice } from "../../../../api/admin-notification";
import { NoticeModal } from "./patch-modal/modal";
import { usePage } from "../../../../hooks/usePage";

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`;

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`;

const MainTitleText = styled.div`
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`;

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${buttonStyle}
`;

const DeleteButton = styled.button`
  ${buttonStyle}
`;

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`;

const DataBox = styled.div`
  width: 100%;
`;

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  // margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`;

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`;

const LogoImage = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const InputBoxWithCount = styled.div`
  position: relative;
`;

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`;

const NoticeBox = styled.button`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 80px;
  border: 1px solid #c1c2c3;
  background: #FFFFFF;
`;

const NoticeTitle = styled.div`
  font-family: "Spoqa Han Sans Neo";
  text-align: left;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  padding: 4px 0 0 24px;
  color: #000000; 
`;

const NoticeSubTitle = styled.div`
  font-family: "Spoqa Han Sans Neo";
  text-align: left;
  font-style: normal;
  font-weight: 500;
  font-size: 14px;
  color: #797A7B;
  padding: 4px 0 0 24px;
`;

const CurrentPageText = styled.button`
  background: #5500DD;
  color:#FFFFFF;
  border: 0px;
  margin-bottom:16px;
`;

const PageText = styled.button`
  background: #FFFFFF;
  color:#C1C2C3;
  border: 0px;
  margin-bottom:16px;
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
`;

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 148px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  resize: none;
  overflow: auto;
`;

const BodyTitleBold = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  color: #454648;
`;

interface NoticeData {
  church_notice_id: string;
  title: string;
  content: string;
  image: string | "";
  created_at: string;
}

interface NoticePropType {
  refresh: boolean;
  setRefresh: Dispatch<SetStateAction<boolean>>;
}
const pagePerLimit = 7
export const InnerListPage = (props: NoticePropType) => {
  const { refresh, setRefresh } = props
  const [noticeData, setNoticeData] = useState<Array<NoticeData>>([{
    church_notice_id: "",
    title: "",
    content: "",
    image: "",
    created_at: "",
  }]);
  const [noticePage, setNoticePage] = useState(0);
  const [currentIndex, setIndex] = useState(0)

  const {pageList,currentPage, getPrevPage, getNextPage, clickPage} = usePage({maxPage: noticePage})

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }

    const fetchData = async () => {
      const response = await getNotice((currentPage - 1) * pagePerLimit);
      setNoticeData(response.payload.church_notice_list.results);
      setNoticePage(Math.ceil(response.payload.church_notice_list.total_count / pagePerLimit))
    };

    fetchData();
  }, [refresh]);

  const getNoticePage = async (page: number) => {
    // setCurrentPage(page)
    const response = await getNotice((page-1) * pagePerLimit);
    setNoticeData(response.payload.church_notice_list.results);
  }

  useEffect(() => {
    getNoticePage(currentPage)
  },[currentPage])

  // Modal
  const [showModal, setShowModal] = useState(false);

  const handleButtonClick = (index: number) => {
    setShowModal(true);
    setIndex(index)
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <Container>
      <Header>
        <MainTitleText>공지사항 목록</MainTitleText>
      </Header>
      <DataBoxContainer>
        <DataContainer><DataBox><InfoBox>
          <img src="/source/icon/information.svg" alt="information" />
          <InfoTitle>박스를 클릭하면 수정할 수 있어요.</InfoTitle>
        </InfoBox></DataBox>
        </DataContainer>
        <DataContainer>
          <DataBox>
            {noticeData && noticeData.map((notice,index) => {
              return <NoticeBox onClick={() => handleButtonClick(index)}>
                <NoticeTitle>{notice.title}</NoticeTitle>
                <NoticeSubTitle>{notice.created_at.substring(0, 10).replace(/-/gi, '.')}</NoticeSubTitle>

              </NoticeBox>
            })}
          </DataBox>
        </DataContainer>
        {showModal && <NoticeModal
          refresh = {refresh}
          setRefresh={setRefresh}
          onCloseModal={handleCloseModal}
          church_notice_id={noticeData[currentIndex].church_notice_id}
          notice_title={noticeData[currentIndex].title}
          notice_content={noticeData[currentIndex].content}
          notice_image={noticeData[currentIndex].image}
        />}
        <div
          style={{
            height: "48px",
          }}
        />
        <img onClick={getPrevPage} src="/source/icon/arrow_left_regular.svg" alt="information" />
        {pageList.map((page) => {
          if (page === currentPage) { return <CurrentPageText>{page}</CurrentPageText> }
          else { return <PageText onClick={() => clickPage(page)}>{page}</PageText>}
        })}
        <img onClick={getNextPage} src="/source/icon/arrow_right_regular.svg" alt="information" />
      </DataBoxContainer>


      <div
        style={{
          height: "48px",
        }}
      />
    </Container >
  );
};
