import React, { Dispatch, SetStateAction, useEffect, useRef, useState } from "react";
import styled from "styled-components";
import Select from 'react-select'
import { setAuthToken } from "../../../../../api";
import { Ref } from "react";
import { PastorImage } from "../pastorImage";

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWrapper = styled.div`
  position: relative;
  width: 700px;
  height: 600px;
  background-color: #ffffff;
  border-radius: 20px;
`;

const ModalCloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #FFFFFF;
  border: none;
  cursor: pointer;
`;

const NoticeModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const ButtonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${ButtonStyle}
`;

const DeleteButton = styled.button`
  ${ButtonStyle}
`;

const WeeklyDateSelect = styled.div`
width: 303px;
height: 56px;
`;

const Image = styled.div`
  margin-top: 24px;
  width: 40px;
  height: 60px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const InputBox = styled.input`
  width: 200px;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const DataBox = styled.div`
  width: 100%;
  display: flex;
  flex-direction: "row";
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  gap: 48px;
  justify-content: space-between;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;


interface PastorModalProps {
  refresh: boolean;
  setRefresh: Dispatch<SetStateAction<boolean>>;
  onCloseModal: () => void;
}

interface Pastor {
    name: string;
    role: string;
    image: string;
    uploadImage: File | undefined;
    imageFile: any;
}


export const PastorCreateModal = (props: PastorModalProps) => {
    const { refresh, setRefresh, onCloseModal } = props;
    const pastorInit = {
        name : "",
        role : "",
        image : "",
        uploadImage: undefined,
        imageFile : useRef<HTMLInputElement>(null)
    }

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }
  }, []);
    const [render,setRender] = useState(false)
    const [pastorList, setPastorList] = useState<Array<Pastor>>([pastorInit])

    const saveImageFile = (event: any, index: number) => {
        console.log(index)
      if (event.target.files !== null && event.target.files[0] !== undefined) {
          pastorList[index] = { ...pastorList[index], uploadImage: event.target.files[0] }
          pastorList[index] = {...pastorList[index], image: URL.createObjectURL(event.target.files[0])}
        setPastorList(pastorList)
        setRender(!render)
    }
  }
    const changeName = (index: number, name: string) => {
        pastorList[index] = {...pastorList[index], name: name}
        setPastorList(pastorList)
        setRender(!render)
    }
    const changeRole = (index: number, role: string) => {
        pastorList[index] = {...pastorList[index], role: role}
        setPastorList(pastorList)
        setRender(!render)
  }

  const onClickSaveButton = async() => {
    // await patchChurchWeekly(uploadImage, church_pastor_id, name, weeklyYear, weeklyMonth)
    setRefresh(!refresh)
    onCloseModal()
  }
  const onClickDeleteButton = async() => {
    // await deleteChurchWeekly(church_pastor_id)
    setRefresh(!refresh)
    onCloseModal()
  }

  return (
      <ModalOverlay >
          <ModalWrapper >
              <ModalCloseButton onClick={onCloseModal}><img src="source/icon/close.svg" alt="close" /></ModalCloseButton>
              <NoticeModalContent>

                  {pastorList && pastorList.map((pastor, index) => {

                      return (
                          <div
                          key={index}>
                        <DataBox>
                                  <PastorImage
                                      index={index}
                                      saveImageFile={saveImageFile}
                                      image={pastor.image}
                                  />
            <InputBox
                type="text"
                value={pastor.name}
                placeholder={"제목 입력"}
                onChange={(e) => changeName(index,e.target.value)}
            />
            <InputBox
                type="text"
                value={pastor.role}
                placeholder={"직분 입력"}
                onChange={(e) => changeRole(index,e.target.value)}
            />
                              </DataBox>
                              </div>
                      )
                  })}


            <DataContainer><DataBox><AddButtonBox><AddButton onClick={() => setPastorList([...pastorList, pastorInit])}
          style={{
            background: "#ffffff",
            border: "1px solid #5500dd",
            color: "#5500dd",
          }}
        >
          <img src="/source/icon/add.svg" alt="information" style={{ marginRight: "8px" }} />
          섬기는 이 추가
        </AddButton></AddButtonBox></DataBox></DataContainer>


          <UploadButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #5500DD",
                color: "#5500DD" ,
              }}
              onClick={onClickSaveButton}
            >
              저장
            </UploadButton>
            <DeleteButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #D40030",
                color: "#D40030" ,
              }}
              onClick={onCloseModal}
            >
              취소
          </DeleteButton>

              </NoticeModalContent>
          </ModalWrapper>
      </ModalOverlay>
  );
};
