import React from 'react'
import {
  Animator,
  ScrollContainer,
  ScrollPage,
  batch,
  Fade,
  FadeIn,
  FadeOut,
  Move,
  MoveIn,
  MoveOut,
  Sticky,
  StickyIn,
  StickyOut,
  Zoom,
  ZoomIn,
  ZoomOut,
} from 'react-scroll-motion'
import styled from 'styled-components'
import { Margin24 } from '../../component/styledComponent'
const Container = styled.div`
  display: flex;
  flex-direction: row;
  width: 1280px;
  height: 600px;
`
const TextContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 600px;
  width: 436px;
  margin-right: 164px;
  padding-top: 137px;
  justify-content: flex-start;
  text-align: left;
`
const Title = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 56px;
  line-height: 72px;
  color: #000000;
  white-space: nowrap;
`
const Description = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 36px;
  color: #656667;
`
const Button = styled.button`
  width: 298px;
  height: 56px;
  background: #5500dd;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.24);
  border-radius: 36px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  text-align: center;
  border: none;
  color: #ffffff;
`
const LeftArrow = styled.div`
  position: absolute;
  left: 0%;
  right: 0%;
  top: 0%;
  bottom: 0%;
`

export const Page1 = () => {
  return (
    <Animator animation={batch(Fade(), Sticky(), MoveOut(0, -200))}>
      <Container>
        <TextContainer>
          <Title>교회 관리를</Title>
          <Title>
            <text style={{ fontWeight: 700, color: '#5500dd' }}>
              빠르고 효율적
            </text>
            으로,
          </Title>
          <Margin24 style={{ backgroundColor: '#ffffff' }} />
          <Description>
            복잡했던 예배 일정 공지, 주보 제작 및 인쇄, 신도 관리를
          </Description>
          <Description>
            쉽고편리하게 교회를 관리할 수 있는 통합 서비스입니다.
          </Description>
          <Margin24 style={{ backgroundColor: '#ffffff' }} />
          <Margin24 style={{ backgroundColor: '#ffffff' }} />
          <Button>
            고객 제휴 문의{' '}
            <LeftArrow>
              <img src="/source/icon/arrow_right_white.svg" />
            </LeftArrow>
          </Button>
        </TextContainer>
        <span style={{ fontSize: '30px' }}>
          Let me show you scroll animat2ion 😀
        </span>
      </Container>
    </Animator>
  )
}
