import React from 'react'
import styled from 'styled-components'
import { Margin16, Margin24 } from '../component/styledComponent'

const Title = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  color: #000000;
  text-align: left;
`
const ContentContainer = styled.div`
  display: flex;
  flex-direction: row;
`

const ContentText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 22px;
  text-align: left;
  color: #000000;
`
interface ContentProps {
  text: string
  isTab: boolean
  isDot: boolean
}
interface TermBoxProps {
  title: string
  contents: Array<ContentProps>
}
export const TermBox = (props: TermBoxProps) => {
  const { title, contents } = props
  return (
    <div>
      <Title>{title}</Title>
      <Margin16 />
      {contents &&
        contents.map(content => {
          return <ContentText>{content.text}</ContentText>
        })}

      <Margin24 style={{ backgroundColor: '#ffffff' }} />
    </div>
  )
}
