import React, { useEffect, useState } from 'react'
import { Week } from '../../../../../hooks/useMonth'
import styled from 'styled-components'
import { useWeek } from '../../../../../hooks/useWeek'

const DayContainer = styled.div`
  width: 128px;
  height: 26px;
`

const WeekDateContainer = styled.div`
  height: 30px;
  display: flex;
  flex-direction: row;
`
const WeekContentContainer = styled.div`
  height: 94px;
`
const DateText = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`

const TestBox = styled.div`
  position: absolute;
  width: 124px;
  height: 26px;
  background: #ffffff;
  border: 1px solid #5500dd;
`
interface DayProps {
  index: number
  date: number
  year: number
  month: number
  isCurrentMonth: boolean
}
interface WeekViewProps {
  week: Array<Week>
  year: number
  month: number
}

const test_calendar = [
  {
    church_calendar_id: '',
    title: 'title1',
    content: 'content2',
    start_date: '2023-02-28',
    end_date: '',
  },
  {
    church_calendar_id: '',
    title: 'title2',
    content: 'content2',
    start_date: '2023-03-01',
    end_date: '2023-03-05',
  },
  {
    church_calendar_id: '',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '',
  },
]

const Day = (props: DayProps) => {
  const { index, date, isCurrentMonth, year, month } = props
  return (
    <DayContainer>
      <DateText
        style={{
          color: !isCurrentMonth
            ? '#c1c2c3'
            : index === 0
            ? '#D40030'
            : '#000000',
        }}
      >
        {date}
      </DateText>
    </DayContainer>
  )
}

export const WeekView = (props: WeekViewProps) => {
  const { week, year, month } = props
  useWeek(week, test_calendar)
  //   console.log(week)
  return (
    <>
      <WeekDateContainer>
        {week &&
          week.map((day, index) => {
            return (
              <Day
                index={index}
                date={day.date}
                year={year}
                month={month}
                isCurrentMonth={day.isCurrentMonth}
              />
            )
          })}
      </WeekDateContainer>

      <WeekContentContainer>
        <TestBox>123</TestBox>
        <TestBox style={{ marginLeft: '130px' }}>123</TestBox>
        <TestBox>123</TestBox>
      </WeekContentContainer>
    </>
  )
}
