import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useState } from 'react'
import { setAuthToken } from '../../../../api'
import {
  getChurchInfo,
  patchChurchAddress,
} from '../../../../api/admin-information'
import {
  FlexRowContainer,
  FlexRowInnerLeft,
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  MainContainer,
} from '../../../../component/styledComponent'

const SubTitleText = styled.div`
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  cursor: pointer;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
`

const InputBoxWithCount = styled.div`
  position: relative;
`

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 140px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  resize: none;
  overflow: auto;
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`

export const InnerAddressPage: React.FC = () => {
  const [editState, setEditState] = useState(false)
  const [ownCar, setOwncar] = useState('')
  const [parking, setParking] = useState('')
  const [publicTransport, setPublicTransport] = useState('')
  const [shuttleBus, setShuttleBus] = useState('')
  const [parkingCount, setParkingCount] = useState(0)
  const [onCarCount, setOncarCount] = useState(0)
  const [publicTransportCount, setPublicTransportCount] = useState(0)
  const [shuttleBusCount, setShuttleBusCount] = useState(0)

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)

      const fetchData = async () => {
        const response = await getChurchInfo()
        setOwncar(response.payload.church_info.directions_own_car)
        setParking(response.payload.church_info.directions_parking)
        setPublicTransport(
          response.payload.church_info.directions_public_transport
        )
        setShuttleBus(response.payload.church_info.directions_shuttle_bus)
        setParkingCount(response.payload.church_info.directions_parking.length)
        setOncarCount(response.payload.church_info.directions_own_car.length)
        setPublicTransportCount(
          response.payload.church_info.directions_public_transport.length
        )
        setShuttleBusCount(
          response.payload.church_info.directions_shuttle_bus.length
        )
      }

      fetchData()
    }
  }, [])

  const handleEdit = () => {
    if (editState) {
      setEditState(false)
      patchChurchAddress(parking, ownCar, publicTransport, shuttleBus)
      window.location.reload()
    } else {
      setEditState(true)
    }
  }

  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>오시는 길</InnerBoxTitle>
        <ModifyButton
          onClick={handleEdit}
          style={{
            background: editState ? '#5500DD' : '#ffffff',
            border: editState ? 'none' : '1px solid #5500dd',
            color: editState ? '#ffffff' : '#5500dd',
          }}
        >
          {editState ? '저장' : '수정'}
        </ModifyButton>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <FlexRowContainer>
          <FlexRowInnerLeft>
            <SubTitleText>주차장</SubTitleText>
            <InputBoxWithCount>
              <InputSubBox
                maxLength={100}
                value={parking}
                disabled={!editState}
                placeholder="주차장 안내 입력"
                onChange={e => {
                  setParking(e.target.value)
                  setParkingCount(e.target.value.length)
                }}
              />
              <InputCount>({parkingCount}/100)</InputCount>
            </InputBoxWithCount>
            <SubTitleText>대중교통</SubTitleText>
            <InputBoxWithCount>
              <InputSubBox
                maxLength={100}
                value={publicTransport}
                disabled={!editState}
                placeholder="대중교통 안내 입력"
                onChange={e => {
                  setPublicTransport(e.target.value)
                  setPublicTransportCount(e.target.value.length)
                }}
              />
              <InputCount>({publicTransportCount}/100)</InputCount>
            </InputBoxWithCount>
          </FlexRowInnerLeft>
          <SubTitleText>자가용</SubTitleText>
          <InputBoxWithCount>
            <InputSubBox
              maxLength={100}
              value={ownCar}
              disabled={!editState}
              placeholder="자가용 안내 입력"
              onChange={e => {
                setOwncar(e.target.value)
                setOncarCount(e.target.value.length)
              }}
            />
            <InputCount>({onCarCount}/100)</InputCount>
          </InputBoxWithCount>

          <SubTitleText>셔틀버스</SubTitleText>
          <InputBoxWithCount>
            <InputSubBox
              maxLength={100}
              value={shuttleBus}
              disabled={!editState}
              placeholder="셔틀버스 안내 입력"
              onChange={e => {
                setShuttleBus(e.target.value)
                setShuttleBusCount(e.target.value.length)
              }}
            />
            <InputCount>({shuttleBusCount}/100)</InputCount>
          </InputBoxWithCount>
        </FlexRowContainer>
        <div
          style={{
            height: '48px',
          }}
        />
      </InnerBoxContainer>
      <div
        style={{
          height: '48px',
        }}
      />
    </MainContainer>
  )
}
