import { instance } from "..";

export const getWeekly = async (offset: number) => {
  try {
    const response = await instance.get("administrators/churchs/weekly/", { params: { offset } }
    );
    return response.data;
  } catch (error) {
    console.error(error);
  }
};

export const createChurchWeekly = async (image: any, title: string | undefined, year: string | undefined, month: string | undefined) => {
  const formData = new FormData();
  image.length !== 0 && formData.append('image', image, image.name);
  title !== undefined && formData.append('title', title);
  year !== undefined && formData.append('year', year);
  month !== undefined && formData.append('month', month);
  try {
    const response = await instance.post("administrators/churchs/weekly/", formData);
    return response.data;
  } catch (error) {
    console.error(error);
  }
};

export const patchChurchWeekly = async (image: any, church_weekly_id: string | undefined, title: string | undefined, year: string | undefined, month: string | undefined) => {
  const formData = new FormData();
  image.length !== 0 && typeof image[0] === typeof true ? formData.append('is_delete_image', "false") : formData.append('image', image, image.name);
  church_weekly_id !== undefined && formData.append('church_weekly_id', church_weekly_id);
  title !== undefined && formData.append('title', title);
  year !== undefined && formData.append('year', year);
  month !== undefined && formData.append('month', month);

  try {
    const response = await instance.patch("administrators/churchs/weekly/", formData);
    return response.data;
  } catch (error) {
    console.error(error);
  }
};
