import React from "react";
import styled from "styled-components";

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWrapper = styled.div`
  position: relative;
  width: 500px;
  height: 300px;
  background-color: #ffffff;
  border-radius: 20px;
`;

const ModalCloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #FFFFFF;
  border: none;
  cursor: pointer;
`;

const NoticeModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

interface NoticeModalProps {
  onCloseModal: () => void;
  church_notice_id: string;
  title: string;
  content: string;
  image: string;
}

export const NoticeModal = (props: NoticeModalProps) => {
    const { onCloseModal, church_notice_id, title, content, image} = props;

    return (
        <ModalOverlay >
            <ModalWrapper >
                <ModalCloseButton onClick={onCloseModal}><img src="source/icon/close.svg" alt="close" /></ModalCloseButton>
                <NoticeModalContent>
                  <h2>{church_notice_id}</h2>
                  <h2>{title}</h2>
                  <h2>{content}</h2>
                  <h2>{image}</h2>      
                </NoticeModalContent>
            </ModalWrapper>
        </ModalOverlay>
    );
};
