import { useCallback, useEffect, useRef, useState } from 'react'

interface Date {
  date: number
  month: number
  year: number
}
interface CalendarList {
  church_calendar_id: string
  title: string
  content: string
  start_date: string
  end_date: string
}

const ableMatrixInit = [
  [true, true, true, true, true, true, true],
  [true, true, true, true, true, true, true],
  [true, true, true, true, true, true, true],
]
const numRow = 3
export const useWeek = (
  week: Array<Date>,
  year: number,
  month: number,
  calendarList: Array<CalendarList>
) => {
  const [ableMatrix, setAbleMatrix] = useState<boolean[][]>(ableMatrixInit)
  const [weekStartDate, setWeekStartDate] = useState<string>()
  const [weekEndDate, setWeekEndDate] = useState<string>()
  useEffect(() => {
    var year = week[0].year.toString()
    var month = week[0].month.toString()
    month = month.length === 2 ? month : '0' + month
    var date = week[0].date.toString()
    date = date.length === 2 ? date : '0' + date
    setWeekStartDate(year + '-' + month + '-' + date)
    var year = week[6].year.toString()
    var month = week[6].month.toString()
    month = month.length === 2 ? month : '0' + month
    var date = week[6].date.toString()
    date = date.length === 2 ? date : '0' + date
    setWeekEndDate(year + '-' + month + '-' + date)
  }, [week])
  console.log(calendarList)
  console.log(weekStartDate)
}
