import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useState, useRef } from 'react'
import { CreateModal } from './create-modal/modal'
import { PatchModal } from './patch-modal/modal'
import { setAuthToken } from '../../../../api'
import { getHistory } from '../../../../api/admin-history'
import { usePage } from '../../../../hooks/usePage'

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`

const SubTitleText = styled.div`
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`

const AddButtonBox = styled.div`
  margin-top: 36px;
  display: flex;
  justify-content: center;
  gap: 16px;
`

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500dd;
  text-align: center;
  cursor: pointer;
`

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`

const UploadButton = styled.button`
  ${buttonStyle}
`

const DeleteButton = styled.button`
  ${buttonStyle}
`

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`

const DataBox = styled.div`
  width: 100%;
`

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`

const LogoImage = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`

const InputBoxWithCount = styled.div`
  position: relative;
`

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
`

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 148px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  resize: none;
  overflow: auto;
`

const BodyTitleBold = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  color: #454648;
`
const HistoryBoxContainer = styled.div`
  display: flex;
  flex-direction: row;
  // margin-top: 36px;
  // margin-left: 36px;
  width: 100%;
  align-items: center;
`

const HistoryBoxDot = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 100px;
  background: #5500dd;
  margin-right: 24px;
`
const HistoryBoxEmptyDot = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 100px;
  background: #ffffff;
  margin-right: 24px;
`
const HistoryBoxDate = styled.text`
  width: 108px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 20px;
  line-height: 28px;
  color: #000000;
  text-align: left;
`
const HistoryBoxContent = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 20px;
  line-height: 28px;
  color: #454648;
  text-align: left;
`

interface HistoryBoxProps {
  prevDate: string
  date: string
  content: string
}
const HistoryBox = (props: HistoryBoxProps) => {
  const { prevDate, date, content } = props
  return (
    <HistoryBoxContainer>
      {prevDate === date ? <HistoryBoxEmptyDot /> : <HistoryBoxDot />}
      <HistoryBoxDate>{prevDate === date ? '' : date}</HistoryBoxDate>
      <HistoryBoxContent>{content}</HistoryBoxContent>
    </HistoryBoxContainer>
  )
}

interface History {
  church_history_id: string
  date: string
  content: string
}
const pagePerLimit = 14
export const InnerHistoryPage: React.FC = () => {
  const [refresh, setRefresh] = useState(false)
  const [showPatchModal, setShowPatchModal] = useState(false)
  const [showCreateModal, setShowCreateModal] = useState(false)

  const [historyPage, setHistoryPage] = useState(0)

  const { pageList, currentPage, getPrevPage, getNextPage, clickPage } =
    usePage({ maxPage: historyPage })

  const [historyData, setHistoryData] = useState<Array<History>>([
    {
      church_history_id: '',
      date: '',
      content: '',
    },
  ])

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    const fetchData = async () => {
      const response = await getHistory((currentPage - 1) * pagePerLimit)
      setHistoryData(response.payload.church_history_list.results)
      setHistoryPage(
        Math.ceil(
          response.payload.church_history_list.total_count / pagePerLimit
        )
      )
    }
    fetchData()
  }, [refresh])

  return (
    <Container>
      <Header>
        <MainTitleText>교회 연혁</MainTitleText>
        <ModifyButtonBox>
          <ModifyButton
            onClick={() => setShowPatchModal(true)}
            style={{
              background: '#ffffff',
              border: '1px solid #5500dd',
              color: '#5500dd',
            }}
          >
            수정
          </ModifyButton>
        </ModifyButtonBox>
      </Header>
      <DataBoxContainer>
        <DataContainer>
          <DataBox>
            {historyData &&
              historyData.map((history, index) => {
                return (
                  <HistoryBox
                    prevDate={index !== 0 ? historyData[index - 1].date : ''}
                    date={history.date}
                    content={history.content}
                  />
                )
              })}
            <AddButtonBox>
              <AddButton
                onClick={() => setShowCreateModal(true)}
                style={{
                  background: '#ffffff',
                  border: '1px solid #5500dd',
                  color: '#5500dd',
                }}
              >
                <img
                  src="/source/icon/add.svg"
                  alt="information"
                  style={{ marginRight: '8px' }}
                />
                교회 연혁 추가
              </AddButton>
            </AddButtonBox>
          </DataBox>
          {showPatchModal && (
            <PatchModal
              refresh={refresh}
              setRefresh={setRefresh}
              onCloseModal={() => setShowPatchModal(false)}
            />
          )}
          {showCreateModal && (
            <CreateModal
              refresh={refresh}
              setRefresh={setRefresh}
              onCloseModal={() => setShowCreateModal(false)}
            />
          )}
        </DataContainer>
        <div
          style={{
            height: '48px',
          }}
        />
      </DataBoxContainer>
      <div
        style={{
          height: '48px',
        }}
      />
    </Container>
  )
}
