import React from 'react'
import styled from 'styled-components'
import { Header } from '../main-pages/header'
import { Margin36 } from '../component/styledComponent'
import { TermBox } from './termBox'

const Container = styled.div`
  width: 100%;
`
const Title = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 18px;
  color: #000000;
`
const BodyContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 0 auto;
  width: 1280px;
  height: 800px;
`

const Body = styled.div`
  margin-left: 100px;
  text-align: left;
`

const BodyImg = styled.div`
  margin-right: 100px;
  display: flex;
  align-items: flex-end;
`

const BodyTitle = styled.div`
  margin-top: 260px;
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 48px;
  line-height: 60px;
  color: #000000;
`

const BodyTitleBold = styled.text`
  ffont-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 48px;
  line-height: 60px;
`

const BodySubTitle = styled.div`
  margin-top: 24px;
  text-align: left;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 24px;
  line-height: 32px;
  color: #797a7b;
`

const SubButton = styled.button`
  margin-top: 64px;
  width: 168px;
  height: 48px;
  background: #5500dd;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  color: #ffffff;
  border: none;
`

export const Location = () => {
  return (
    <Container>
      <Header />
      <Margin36 />
      <BodyContainer>
        <TermBox
          title="제1조(목적)"
          content='본 약관은 서비스 이용약관에 동의한 이용자(이하 "회원" 또는 "사용자")가 업텐(이하 "회사")에서 제공하는 주다 서비스(이하 "서비스"라 하며, 추후 서비스명은 변경될 수 있음)를 이용함에 있어 회사와 회원 간 또는 회원 간의 권리, 의무 및 책임사항 및 서비스 이용조건 및 절차 등 기본적인 사항을 규정함을 목적으로 합니다. 회사가 서비스 이용약관을 개정하는 경우에는 홈페이지 또는 애플리케이션에 게시하거나 개별적으로 공지할 것입니다.'
        />
        <TermBox
          title="제2조(제공 서비스)"
          content="회사가 제공하는 서비스는 다음과 같습니다.

          - 서비스명 : 주다(jooda)
          - 서비스 내용 : 교회 통합 정보 플랫폼"
        />
      </BodyContainer>
    </Container>
  )
}
