import React, { useEffect, useState } from 'react'
import {
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  MainContainer,
  Margin36,
  SubTitle,
} from '../../../../../component/styledComponent'
import { Date, useMonth } from '../../../../../hooks/useMonth'
import { WeekView } from './week'
import styled from 'styled-components'

const WeekHeader = styled.div`
  height: 48px;
  display: flex;
  flex-direction: row;
`
const WeekHeaderText = styled.text`
  width: 128px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`

export const CalendarHeader = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
`
const test_calendar = [
  {
    church_calendar_id: '0',
    title: 'titl',
    content: 'content2',
    start_date: '2023-01-30',
    end_date: '',
    is_current_month: false,
  },
  {
    church_calendar_id: '1',
    title: 'title1',
    content: 'content2',
    start_date: '2023-02-27',
    end_date: '2023-02-28',
    is_current_month: false,
  },
  {
    church_calendar_id: '1',
    title: 'title1',
    content: 'content2',
    start_date: '2023-02-28',
    end_date: '',
    is_current_month: false,
  },
  {
    church_calendar_id: '2',
    title: 'title2운마ㅓㅜ아ㅓㄴ무아ㅓㄴ무안무ㅏㅓ우나무아ㅓㄴ무ㅏ',
    content: 'content2',
    start_date: '2023-03-01',
    end_date: '2023-03-05',
    is_current_month: true,
  },
  {
    church_calendar_id: '3',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '2023-03-03',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: '4월1일텏스트으',
    content: 'content4',
    start_date: '2023-03-04',
    end_date: '',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '2023-03-02',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: '4월1일텏스트으',
    content: 'content4',
    start_date: '2023-04-01',
    end_date: '2023-04-02',
    is_current_month: true,
  },
]

const weekHeader = ['일', '월', '화', '수', '목', '금', '토']
export const CalendarView = () => {
  const { weekList, year, month, setYear, setMonth } = useMonth()

  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>달력</InnerBoxTitle>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <Margin36 />
        <CalendarHeader>
          <SubTitle>
            {year}년 {month}월
          </SubTitle>
          <div>
            <div onClick={() => setMonth(month - 1)}>지난달</div>
            <div onClick={() => setMonth(month + 1)}>다음달</div>
          </div>
        </CalendarHeader>

        <Margin36 />
        <WeekHeader>
          {weekHeader.map((haederText, index) => {
            return <WeekHeaderText key={index}>{haederText}</WeekHeaderText>
          })}
        </WeekHeader>
        {weekList &&
          weekList.map((week: Date[], index: number) => {
            return (
              <WeekView
                key={index}
                week={week}
                year={year}
                month={month}
                calendar_list={test_calendar}
              />
            )
          })}
      </InnerBoxContainer>
      <div style={{ paddingTop: '24px' }} />
    </MainContainer>
  )
}
