import React, { useEffect, useState } from 'react'
import { Date } from '../../../../../hooks/useMonth'
import styled from 'styled-components'
import { useWeek } from '../../../../../hooks/useWeek'

const DayContainer = styled.div`
  width: 128px;
  height: 26px;
`

const WeekDateContainer = styled.div`
  height: 30px;
  display: flex;
  flex-direction: row;
`
const WeekContentContainer = styled.div`
  height: 94px;
`
const DateText = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
`

const ScheduleBox = styled.div`
  margin-top: 2px;
  border-radius: 8px;
  background: #5500dd;
  position: absolute;
  height: 26px;
  text-align: left;
  justify-content: center;
  align-item: center;
  color: #ffffff;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 26px;
  text-align: left;
  padding-left: 4px;
  overflow: hiddlen;
  display: inline-block;
`

interface DayProps {
  index: number
  date: Date
  currentYear: number
  currentMonth: number
}
interface WeekViewProps {
  week: Array<Date>
  year: number
  month: number
}

const test_calendar = [
  {
    church_calendar_id: '0',
    title: 'titl',
    content: 'content2',
    start_date: '2023-01-30',
    end_date: '',
    is_current_month: false,
  },
  {
    church_calendar_id: '1',
    title: 'title1',
    content: 'content2',
    start_date: '2023-02-28',
    end_date: '',
    is_current_month: false,
  },
  {
    church_calendar_id: '2',
    title: 'title2운마ㅓㅜ아ㅓㄴ무아ㅓㄴ무안무ㅏㅓ우나무아ㅓㄴ무ㅏ',
    content: 'content2',
    start_date: '2023-03-01',
    end_date: '2023-03-05',
    is_current_month: true,
  },
  {
    church_calendar_id: '3',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '2023-03-03',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: '4월1일텏스트으',
    content: 'content4',
    start_date: '2023-03-04',
    end_date: '',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: 'title3',
    content: 'content4',
    start_date: '2023-03-01',
    end_date: '2023-03-02',
    is_current_month: true,
  },
  {
    church_calendar_id: '4',
    title: '4월1일텏스트으',
    content: 'content4',
    start_date: '2023-04-01',
    end_date: '2023-04-02',
    is_current_month: true,
  },
]

const Day = (props: DayProps) => {
  const { index, date, currentYear, currentMonth } = props
  return (
    <DayContainer>
      <DateText
        style={{
          color:
            date.month !== currentMonth
              ? '#c1c2c3'
              : index === 0
              ? '#D40030'
              : '#000000',
        }}
      >
        {date.date}
      </DateText>
    </DayContainer>
  )
}
// const scheduleWidth = [0, 124, 252, 380, 508, 636, 764, 892]
const scheduleWidth = [0, 120, 248, 376, 504, 632, 760, 888]
export const WeekView = (props: WeekViewProps) => {
  const { week, year, month } = props
  const { weekData } = useWeek(week, test_calendar, year, month)

  return (
    <>
      <WeekDateContainer>
        {week &&
          week.map((day, index) => {
            return (
              <Day
                key={index}
                index={index}
                date={day}
                currentYear={year}
                currentMonth={month}
              />
            )
          })}
      </WeekDateContainer>

      <WeekContentContainer>
        {weekData.length !== 0 &&
          weekData.map(schedule => {
            // console.log('length', schedule.length)
            return (
              <ScheduleBox
                style={{
                  marginLeft: schedule.x * 128 + 2,
                  marginTop: schedule.y * 26 + schedule.y * 2,
                  width: scheduleWidth[schedule.length],
                  backgroundColor:
                    schedule.length === 1
                      ? '#ffffff'
                      : schedule.is_current_month
                      ? '#5500dd'
                      : '#D8C2F9',
                  color:
                    schedule.length !== 1
                      ? '#ffffff'
                      : schedule.is_current_month
                      ? '#5500dd'
                      : '#D8C2F9',
                  borderColor: schedule.is_current_month
                    ? '#5500dd'
                    : '#D8C2F9',
                  border: '1px solid',
                  textOverflow: 'ellipsis',
                }}
              >
                {schedule.title}
              </ScheduleBox>
            )
          })}
      </WeekContentContainer>
    </>
  )
}
