import React, { useEffect, useState } from 'react'
import { Week } from '../../../../../hooks/useMonth'
import styled from 'styled-components'

const DayContainer = styled.div`
  width: 128px;
  height: 124px;
`
const WeekContainer = styled.div`
  height: 124px;
  display: flex;
  flex-direction: row;
`
const DateText = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  color: #c1c2c3;
`
interface Day {
  index: number
  date: number
  isCurrentMonth: boolean
}
interface WeekViewProps {
  week: Array<Week>
}

const Day = (props: Day) => {
  const { date, isCurrentMonth } = props
  return (
    <DayContainer>
      {date}
      {'  '}
      {isCurrentMonth}
    </DayContainer>
  )
}

export const WeekView = (props: WeekViewProps) => {
  const { week } = props

  return (
    <WeekContainer>
      {week &&
        week.map((day, index) => {
          return (
            <Day
              index={index}
              date={day.date}
              isCurrentMonth={day.isCurrentMonth}
            />
          )
        })}
    </WeekContainer>
  )
}
