import { instance } from '..'

export const getPastor = async (offset: number) => {
  try {
    const response = await instance.get('administrators/churchs/pastor/', {
      params: { offset },
    })
    return response.data
  } catch (error) {
    console.error(error)
  }
}

export const createChurchPastors = async (
  pastor_list: Array<any>,
  image_list: Array<File | undefined>
) => {
  const formData = new FormData()
  console.log(JSON.stringify(pastor_list))
  pastor_list.forEach(pastor => {
    formData.append('pastor_list', JSON.stringify(pastor))
  })
  image_list.forEach(file => {
    formData.append('image_list', file)
  })

  try {
    const response = await instance.post(
      'administrators/churchs/pastor/',
      formData
    )
    return response.data
  } catch (error) {
    console.error(error)
  }
}

export const patchChurchWeekly = async (
  image: any,
  church_weekly_id: string | undefined,
  title: string | undefined,
  year: string | undefined,
  month: string | undefined
) => {
  const formData = new FormData()
  image.length !== 0 && formData.append('image', image)
  church_weekly_id !== undefined &&
    formData.append('church_weekly_id', church_weekly_id)
  title !== undefined && formData.append('title', title)
  year !== undefined && formData.append('year', year)
  month !== undefined && formData.append('month', month)

  try {
    const response = await instance.patch(
      'administrators/churchs/weekly/',
      formData
    )
    console.log(response.data)
    return response.data
  } catch (error) {
    console.error(error)
  }
}
