import { Dispatch, SetStateAction, useRef, useState } from 'react'
import styled from 'styled-components'

const Container = styled.div`
  width: 642px;
  height: 110px;
  display: flex;
  flex-direction: row;
  align-items: center;
  border: 1px solid #c1c2c3;
  border-radius: 16px;
  padding-left: 24px;
  margin-bottom: 16px;
  margin-left: 36px;
  margin-right: 36px;
`

const HistoryBoxDot = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 100px;
  background: #5500dd;
  margin-right: 24px;
`

const InputBox = styled.input`
  width: 200px;
  box-sizing: border-box;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
  margin-right: 24px;
`

interface HistoryProps {
  index: number
  date: string
  content: string
  changeDate: Function
  changeContent: Function
  deleteHistroy: Function
}

export const HistoryInputBox = (props: HistoryProps) => {
  const { index, date, content, changeDate, changeContent, deleteHistroy } =
    props

  return (
    <Container>
      <HistoryBoxDot />

      <InputBox
        type="text"
        value={date}
        placeholder={'00.10.01'}
        onChange={e => changeDate(index, e.target.value)}
      />
      <InputBox
        type="text"
        value={content}
        placeholder={'내용 입력'}
        onChange={e => changeContent(index, e.target.value)}
      />
    </Container>
  )
}
