import React, { Dispatch, SetStateAction, useEffect, useState } from 'react'
import {
  FlexRowContainer,
  FlexRowInnerLeft,
  FlexRowInnerRight,
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  InputBox,
  InputBoxWithCount,
  InputBoxWithError,
  InputCount,
  MainContainer,
  Margin16,
  Margin24,
  Margin36,
  SubTitle,
  SubTitleColorText,
} from '../../../../../component/styledComponent'
import styled from 'styled-components'
import { useFormatTime } from '../../../../../hooks/useFormatTime'
import { createSchedule } from '../../../../../api/admin-schedule'
import { setAuthToken } from '../../../../../api'

const ActiveRegisterButton = styled.button`
  color: #ffffff;
  background-color: #5500dd;
  width: 120px;
  height: 40px;
  border: none;
  cursor: pointer;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
`
const DeActiveRegisterButton = styled.button`
  color: #5500dd;
  width: 120px;
  height: 40px;
  border: none;
  cursor: pointer;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  border: 1px solid #5500dd;
`

const Time = styled.div`
  width: calc(50% - 18px);
`
const TimeDivider = styled.div`
  width: 36px;
  margin-top: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
`
const DaysContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`
const DayContainer = styled.div`
  display: flex;
  flex-direction: row;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-size: 16px;
  line-height: 24px;
  background: #ffffff;
`

interface InnerCalendarProps {
  refresh: boolean
  setRefresh: Dispatch<SetStateAction<boolean>>
}

export const InnerCalendarRegist = (props: InnerCalendarProps) => {
  const { refresh, setRefresh } = props
  const [title, setTitle] = useState<string>('')
  const [subtitle, setSubtitle] = useState<string>('')
  const [weekday, setWeekday] = useState<string>('')
  const [place, setPlace] = useState<string>('')
  const [content, setContent] = useState<string>('')
  const [target, setTarget] = useState<string>('')
  const [reference, setReference] = useState<string>('')
  const [isActive, setIsActive] = useState(false)
  const {
    time: startTime,
    setTime: setStartTime,
    isValid: isStartValid,
    isError: isStartError,
  } = useFormatTime()
  const {
    time: endTime,
    setTime: setEndTime,
    isValid: isEndValid,
    isError: isEndError,
  } = useFormatTime()

  const onClickRegisterButton = async () => {
    await createSchedule(
      title,
      subtitle,
      weekday,
      place,
      content,
      target,
      reference,
      startTime,
      isEndValid ? endTime : ''
    )
    setTitle('')
    setSubtitle('')
    setWeekday('')
    setPlace('')
    setMc('')
    setTarget('')
    setReference('')
    setIsActive(false)
    setStartTime('')
    setEndTime('')
    setRefresh(!refresh)
  }
  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }
  }, [])

  useEffect(() => {
    if (title !== '' && weekday !== '' && isStartValid) {
      setIsActive(true)
    } else {
      setIsActive(false)
    }
  }, [title, weekday, isStartValid])
  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>예배 일정 등록</InnerBoxTitle>
        {isActive ? (
          <ActiveRegisterButton onClick={onClickRegisterButton}>
            등록
          </ActiveRegisterButton>
        ) : (
          <DeActiveRegisterButton>등록</DeActiveRegisterButton>
        )}
      </InnerBoxHeader>
      <InnerBoxContainer>
        <Margin36 />

        <SubTitle>제목</SubTitle>
        <InputBox
          type="text"
          maxLength={20}
          value={subtitle}
          placeholder="소제목 입력"
          onChange={e => setSubtitle(e.target.value)}
        />
        <Margin36 />
        <FlexRowContainer>
          <Time>
            <SubTitle>
              날짜<SubTitleColorText> *</SubTitleColorText>
            </SubTitle>
            <InputBoxWithError
              type="text"
              maxLength={5}
              value={startTime}
              error={isStartError}
              placeholder="05:00"
              onChange={e => setStartTime(e.target.value)}
            />
          </Time>
          <TimeDivider>~</TimeDivider>
          <Time>
            <div style={{ paddingTop: '24px' }} />
            <InputBoxWithError
              type="text"
              maxLength={5}
              value={endTime}
              error={isEndError}
              placeholder="06:30"
              onChange={e => setEndTime(e.target.value)}
            />
          </Time>
        </FlexRowContainer>
        <Margin36 />

        <InputBoxWithCount>
          <InputSubBox
            maxLength={200}
            value={content}
            placeholder="내용 입력"
            onChange={e => {
              setContent(e.target.value)
            }}
          />
          <InputCount>({content.length}/200)</InputCount>
        </InputBoxWithCount>
        <Margin36 />

        <Margin36 />
      </InnerBoxContainer>
      <div style={{ paddingTop: '24px' }} />
    </MainContainer>
  )
}
