import { Dispatch, SetStateAction, useRef, useState } from 'react'
import styled from 'styled-components'

const Container = styled.div`
  width: 642px;
  height: 110px;
  display: flex;
  flex-direction: row;
`
const Image = styled.div`
  width: 58px;
  height: 78px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`
const InputBox = styled.input`
  width: 200px;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`

interface PastorImageProps {
  index: number
  image: string
  name: string
  role: string
  saveImageFile: Function
  changeName: Function
  changeRole: Function
}

export const PastorBox = (props: PastorImageProps) => {
  const { index, saveImageFile, image, name, role, changeName, changeRole } =
    props
  const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current) imageFile.current.click()
  }

  return (
    <Container>
      <Image
        style={{
          background: '#ffffff',
          border: '1px solid #5500DD',
          color: '#5500DD',
        }}
        onClick={openImageFile}
      >
        <input
          type="file"
          id="file"
          ref={imageFile}
          style={{ display: 'none' }}
          onChange={e => saveImageFile(e, index)}
        />
        <img
          src={
            image.length === 0 ? '/source/icon/pastor_upload_image.svg' : image
          }
        />
      </Image>
      <InputBox
        type="text"
        value={name}
        placeholder={'제목 입력'}
        onChange={e => changeName(index, e.target.value)}
      />
      <InputBox
        type="text"
        value={role}
        placeholder={'직분 입력'}
        onChange={e => changeRole(index, e.target.value)}
      />
    </Container>
  )
}
