import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import { Page } from '../../../../component/page'
import { usePage } from '../../../../hooks/usePage'
import { useDebounce } from '../../../../hooks/useDebounce'
import { getMember } from '../../../../api/admin-member'
import { MemberBox } from './memberBox'
import { setAuthToken } from '../../../../api'

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`
const MemberContainer = styled.div`
  margin-left: 36px;
  margin-right: 36px;
`

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 0;
  color: ${props => (props.value ? '#000000' : '#797a7b')};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`
const DataBox = styled.div`
  width: 100%;
`

interface Member {
  account_id: string
  birth_date: string
  gender: string
  name: string
  phone_number: string
}

const pagePerLimit = 6
export const InnerMemberPage: React.FC = () => {
  const [memberPage, setMemberPage] = useState(0)
  const [memberData, setMemberData] = useState<Array<Member>>([])
  const { pageList, currentPage, getPrevPage, getNextPage, clickPage } =
    usePage({ maxPage: memberPage })
  const { debounceCallback, isLoading } = useDebounce({ time: 1000 })
  const [keyword, setKeyword] = useState('')
  const [clickedAccountList, setClickedAccountList] = useState<Array<string>>(
    []
  )

  const getChurchMember = async (page: number, keyword: string) => {
    const response = await getMember((page - 1) * pagePerLimit, keyword)
    setMemberData(response.payload.church_members.results)
    setMemberPage(
      Math.ceil(response.payload.church_members.total_count / pagePerLimit)
    )
  }

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    getChurchMember(1, '')
  }, [])

  useEffect(() => {
    if (keyword !== '') {
      debounceCallback(() => {
        setMemberPage(0)
        getChurchMember(1, keyword)
      })
    }
  }, [keyword])

  const onClickMemberBox = (account_id: string) => {
    if (clickedAccountList.indexOf(account_id) > -1) {
      setClickedAccountList(clickedAccountList.filter(id => id !== account_id))
    } else {
      setClickedAccountList([...clickedAccountList, account_id])
    }
  }

  return (
    <Container>
      <Header>
        <MainTitleText>신도 명단</MainTitleText>
      </Header>

      <DataBoxContainer>
        <MemberContainer>
          <InputBox
            type="text"
            value={keyword}
            placeholder={'신도 검색'}
            onChange={e => setKeyword(e.target.value)}
          />

          {memberData &&
            memberData.map(member => {
              return (
                <MemberBox
                  account_id={member.account_id}
                  birth_date={member.birth_date}
                  gender={member.gender}
                  name={member.name}
                  phone_number={member.phone_number}
                  onClickMemberBox={onClickMemberBox}
                />
              )
            })}
        </MemberContainer>

        <div
          style={{
            height: '48px',
          }}
        />
        <Page
          pageList={pageList}
          currentPage={currentPage}
          getPrevPage={getPrevPage}
          getNextPage={getNextPage}
          clickPage={clickPage}
        />
      </DataBoxContainer>
    </Container>
  )
}
