import React, { useEffect, useRef, useState } from 'react'
import styled from 'styled-components'
import { setAuthToken } from '../../../../../../api'
import { getPastor } from '../../../../../../api/admin-pastor'
import InfiniteScroll from 'react-infinite-scroll-component'
import { PastorBox } from '../../pastorBox'
import Modal from './test'

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`

const ModalWrapper = styled.div`
  position: relative;
  width: 738px;
  min-height: 300px;
  background-color: #ffffff;
  border-radius: 20px;
`

const MODALSTYLES = styled.div`
  position: 'absolute',
  backgroundColor: '#FFF',
  padding: '15px',
  zIndex: '1000',
  width: '35%',
  borderRadius: '.5em',
  `

const OVERLAYSTYLE = {
  position: 'fixed',
  display: 'flex',
  justifyContent: 'center',
  top: '0',
  left: '0',
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0,0,0, .8)',
  zIndex: '1000',
  overflowY: 'auto',
}

const ModalCloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #ffffff;
  border: none;
  cursor: pointer;
`
const PastorContainer = styled.div`
  height: 4;
`
const NoticeModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`
interface PastorData {
  church_pastor_id: string
  order: string
  name: string
  role: string
  image: string
}
interface NoticeModalProps {
  onCloseModal: () => void
}

export const PatchOrderModal = (props: NoticeModalProps) => {
  const { onCloseModal } = props
  const offset = useRef<number>(0)
  const maxCount = useRef<number>(0)
  const [pastorData, setPastorData] = useState<Array<PastorData>>([
    {
      church_pastor_id: '',
      order: '',
      name: '',
      role: '',
      image: '',
    },
  ])

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    const fetchData = async () => {
      const response = await getPastor(offset.current)
      setPastorData(response.payload.church_pastor_list.results)
      maxCount.current = response.payload.church_pastor_list.total_count
    }
    fetchData()
  }, [])

  const loadMoreItem = async () => {
    if (offset.current + 15 < maxCount.current) {
      const response = await getPastor(offset.current)
      setPastorData([
        ...pastorData,
        response.payload.church_pastor_list.results,
      ])
    }
  }

  useEffect(() => {
    document.body.style.cssText = `
      position: fixed; 
      top: -${window.scrollY}px;
      overflow-y: scroll;
      width: 100%;`
    return () => {
      const scrollY = document.body.style.top
      document.body.style.cssText = ''
      window.scrollTo(0, parseInt(scrollY || '0', 10) * -1)
    }
  }, [])

  return (
    <MODALSTYLES>
      <ModalCloseButton onClick={onCloseModal}>
        <img src="source/icon/close.svg" alt="close" />
      </ModalCloseButton>
      <NoticeModalContent>
        <MainTitleText>섬기는 이 순서변경</MainTitleText>
        <PastorContainer>
          {pastorData.length !== 0 && (
            <InfiniteScroll
              dataLength={pastorData.length}
              next={loadMoreItem}
              hasMore={offset.current + 15 < maxCount.current ? true : false}
              loader={<h4>Loading...</h4>}
            >
              {pastorData.map((pastor, index) => {
                return (
                  <PastorBox
                    index={index}
                    image={pastor.image}
                    name={pastor.name}
                    role={pastor.role}
                    saveImageFile={loadMoreItem}
                    changeName={loadMoreItem}
                    changeRole={loadMoreItem}
                  />
                )
              })}
            </InfiniteScroll>
          )}
        </PastorContainer>
      </NoticeModalContent>
    </MODALSTYLES>
  )
}
