import React, { Dispatch, SetStateAction, useEffect } from "react";
import styled from "styled-components";
import { useState, useRef } from "react";
import { setAuthToken } from "../../../../api";
import { getWeekly } from "../../../../api/admin-weekly";
import { WeeklyModal } from "./patch-modal/modal";

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`;

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`;

const MainTitleText = styled.div`
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`;

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${buttonStyle}
`;

const DeleteButton = styled.button`
  ${buttonStyle}
`;

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`;

const DataBox = styled.div`
  width: 100%;
`;

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  // margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`;

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`;

const LogoImage = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const InputBoxWithCount = styled.div`
  position: relative;
`;

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`;

const WeeklyBox = styled.div`
  width: 100%;
  box-sizing: border-box;
  border-radius: 16px;
  height: 110px;
  border: 1px solid #c1c2c3;
  display: flex;
`;

const WeeklyImage = styled.div`
  width: 58px;
  height: 78px;
  border-radius: 9px;
  background: #000000;
  margin-left: 24px;
  margin-top: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const WeeklyInnerBox = styled.div`
  margin-top: 15px;
`;

const WeeklyTitle = styled.div`
  font-family: "Spoqa Han Sans Neo";
  text-align: left;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  padding: 16px 0 0 24px;
  color: #000000;
`;

const WeeklySubTitle = styled.div`
  font-family: "Spoqa Han Sans Neo";
  text-align: left;
  font-style: normal;
  font-weight: 500;
  font-size: 14px;
  line-height: 24px;
  color: #797A7B;
  padding: 0 0 0 24px;
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
`;

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 148px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  resize: none;
  overflow: auto;
`;

const BodyTitleBold = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  color: #454648;
`;

interface WeeklyData {
  church_weekly_id: string;
  title: string;
  image: string;
  created_at: string;
}

interface WeeklyPropType {
  refresh: boolean;
  setRefresh: Dispatch<SetStateAction<boolean>>;
}

export const InnerListPage = (props: WeeklyPropType) => {
  const { refresh, setRefresh } = props
  const [weeklyData, setWeeklyData] = useState<Array<WeeklyData>>([{
    church_weekly_id: "",
    title: "",
    image: "",
    created_at: "",
  }]);
  const [weeklyPage, setWeeklyPage] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }

    const fetchData = async () => {
      const response = await getWeekly((currentPage - 1) * 7);
      setWeeklyData(response.payload.church_weekly_list.results);
      setWeeklyPage(Math.floor(response.payload.church_weekly_list.total_count / 7 + 1))
    };


    fetchData();
  }, [refresh]);

  const getWeeklyPage = async (page: number) => {
    setCurrentPage(page)
    const response = await getWeekly(page * 7);
    setWeeklyData(response.payload.church_weekly_list.results);
  }

  // Modal
  const [showModal, setShowModal] = useState(false);

  const handleButtonClick = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <Container>
      <Header>
        <MainTitleText>주보 목록</MainTitleText>
      </Header>
      <DataBoxContainer>
        <DataContainer><DataBox><InfoBox>
          <img src="/source/icon/information.svg" alt="information" />
          <InfoTitle>박스를 클릭하면 수정할 수 있어요.</InfoTitle>
        </InfoBox></DataBox>
        </DataContainer>
        <DataContainer>
          <DataBox>
            {(weeklyData && weeklyData[0].title !== "") && weeklyData.map((weekly) => {
              return <WeeklyBox onClick={handleButtonClick}>
                <WeeklyImage><img src={weekly.image} alt="" /></WeeklyImage>
                <WeeklyInnerBox>
                  <WeeklyTitle>{weekly.title}</WeeklyTitle>
                  <WeeklySubTitle>{weekly.created_at.substring(0, 10).replace(/-/gi, '.')}</WeeklySubTitle>
                </WeeklyInnerBox>
              </WeeklyBox>

            })}
          </DataBox>
        </DataContainer>
        {showModal && <WeeklyModal
          refresh = {refresh}
          setRefresh={setRefresh}
          onCloseModal={handleCloseModal}
          church_notice_id={noticeData[currentIndex].church_notice_id}
          notice_title={noticeData[currentIndex].title}
